import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 19.2-267 Provisions applicable to witnesses in criminal as well as civil cases; obligation to attend; summons.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-267.md","filePath":"vacode/19.2-267.md"}'),r={name:"vacode/19.2-267.md"},o=i('<h1 id="§-19-2-267-provisions-applicable-to-witnesses-in-criminal-as-well-as-civil-cases-obligation-to-attend-summons" tabindex="-1">§ 19.2-267 Provisions applicable to witnesses in criminal as well as civil cases; obligation to attend; summons. <a class="header-anchor" href="#§-19-2-267-provisions-applicable-to-witnesses-in-criminal-as-well-as-civil-cases-obligation-to-attend-summons" aria-label="Permalink to &quot;§ 19.2-267 Provisions applicable to witnesses in criminal as well as civil cases; obligation to attend; summons.&quot;">​</a></h1><p>Sections <a href="http://law.lis.virginia.gov/vacode/8.01-396.1/" target="_blank" rel="noreferrer">8.01-396.1</a>, <a href="http://law.lis.virginia.gov/vacode/8.01-402/" target="_blank" rel="noreferrer">8.01-402</a>, <a href="http://law.lis.virginia.gov/vacode/8.01-405/" target="_blank" rel="noreferrer">8.01-405</a>, <a href="http://law.lis.virginia.gov/vacode/8.01-407/" target="_blank" rel="noreferrer">8.01-407</a>, and <a href="http://law.lis.virginia.gov/vacode/8.01-408/" target="_blank" rel="noreferrer">8.01-408</a> to <a href="http://law.lis.virginia.gov/vacode/8.01-410/" target="_blank" rel="noreferrer">8.01-410</a>, inclusive, shall apply to a criminal as well as a civil case in all respects, except that a witness in a criminal case shall be obliged to attend, and may be proceeded against for failing to do so, although there may not previously have been any payment, or tender to him of anything for attendance, mileage, or tolls. In a criminal case a summons for a witness may be issued by the attorney for the Commonwealth or other attorney charged with the responsibility for the prosecution of a violation of any ordinance or by the attorney for the defendant; however, any attorney who issues such a summons shall, at the time of the issuance, file with the clerk of the court the names and addresses of such witnesses except to the extent protected under § <a href="http://law.lis.virginia.gov/vacode/19.2-11.2/" target="_blank" rel="noreferrer">19.2-11.2</a>.</p><p>Code 1950, § 19.1-262; 1960, c. 366; 1962, c. 374; 1975, c. 495; 1977, c. 624; 1991, c. 38; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0543" target="_blank" rel="noreferrer">543</a>; 2007, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?071+ful+CHAP0552" target="_blank" rel="noreferrer">552</a>; 2008, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0124" target="_blank" rel="noreferrer">124</a>; 2014, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0744" target="_blank" rel="noreferrer">744</a>.</p>',3),n=[o];function s(l,c,h,g,p,d){return a(),t("div",null,n)}const m=e(r,[["render",s]]);export{v as __pageData,m as default};
