import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 19.2-271 Certain judicial officers incompetent to testify under certain circumstances; exceptions (Supreme Court Rule 2:605 derived from this section).","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-271.md","filePath":"vacode/19.2-271.md"}'),o={name:"vacode/19.2-271.md"},n=r('<h1 id="§-19-2-271-certain-judicial-officers-incompetent-to-testify-under-certain-circumstances-exceptions-supreme-court-rule-2-605-derived-from-this-section" tabindex="-1">§ 19.2-271 Certain judicial officers incompetent to testify under certain circumstances; exceptions (Supreme Court Rule 2:605 derived from this section). <a class="header-anchor" href="#§-19-2-271-certain-judicial-officers-incompetent-to-testify-under-certain-circumstances-exceptions-supreme-court-rule-2-605-derived-from-this-section" aria-label="Permalink to &quot;§ 19.2-271 Certain judicial officers incompetent to testify under certain circumstances; exceptions (Supreme Court Rule 2:605 derived from this section).&quot;">​</a></h1><p>No judge shall be competent to testify in any criminal or civil proceeding as to any matter which came before him in the course of his official duties.</p><p>Except as otherwise provided in this section, no clerk of any court, magistrate, or other person having the power to issue warrants, shall be competent to testify in any criminal or civil proceeding as to any matter which came before him in the course of his official duties. Such person shall be competent to testify in any criminal proceeding wherein the defendant is charged with perjury or pursuant to the provisions of § <a href="http://law.lis.virginia.gov/vacode/18.2-460/" target="_blank" rel="noreferrer">18.2-460</a> or in any proceeding authorized pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-353.3/" target="_blank" rel="noreferrer">19.2-353.3</a>. Notwithstanding any other provision of this section, any judge, clerk of any court, magistrate, or other person having the power to issue warrants, who is the victim of a crime, shall not be incompetent solely because of his office to testify in any criminal or civil proceeding arising out of the crime.</p><p>Code 1950, §§ 19.1-267, 19.1-268; 1960, c. 366; 1975, c. 495; 1976, c. 269; 1989, c. 738; 1990, c. 602; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0635" target="_blank" rel="noreferrer">635</a>.</p>',4),a=[n];function c(s,d,f,h,l,p){return t(),i("div",null,a)}const _=e(o,[["render",c]]);export{m as __pageData,_ as default};
