import{_ as t,o,c as a,z as e,a as n}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 19.2-291 Faulty counts; motion to strike; general verdict of guilty.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-291.md","filePath":"vacode/19.2-291.md"}'),r={name:"vacode/19.2-291.md"},i=e("h1",{id:"§-19-2-291-faulty-counts-motion-to-strike-general-verdict-of-guilty",tabindex:"-1"},[n("§ 19.2-291 Faulty counts; motion to strike; general verdict of guilty. "),e("a",{class:"header-anchor",href:"#§-19-2-291-faulty-counts-motion-to-strike-general-verdict-of-guilty","aria-label":'Permalink to "§ 19.2-291 Faulty counts; motion to strike; general verdict of guilty."'},"​")],-1),s=e("p",null,"When there are several counts in the indictment one or more of which are faulty, the accused may move to strike the faulty count or counts or move the court to instruct the jury to disregard them. If he does neither and a general verdict of guilty is found, judgment shall be entered against the accused, if any count be good, though others be faulty, unless the court can plainly see that the verdict could not have been found on the good count. If the accused demurs to the faulty count or moves the court to instruct the jury to disregard it and his demurrer or motion is overruled and there is a general verdict of guilty and it cannot be seen on which count the verdict was founded, if the jury has been discharged, it shall be set aside; but if it is manifest that it could not have been found on the bad count, the verdict shall be allowed to stand.",-1),c=e("p",null,"Code 1950, § 19.1-255; 1960, c. 366; 1975, c. 495.",-1),d=[i,s,c];function u(l,h,f,m,g,v){return o(),a("div",null,d)}const p=t(r,[["render",u]]);export{_ as __pageData,p as default};
