import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 19.2-295.3 Admission of victim impact testimony.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-295.3.md","filePath":"vacode/19.2-295.3.md"}'),o={name:"vacode/19.2-295.3.md"},r=a('<h1 id="§-19-2-295-3-admission-of-victim-impact-testimony" tabindex="-1">§ 19.2-295.3 Admission of victim impact testimony. <a class="header-anchor" href="#§-19-2-295-3-admission-of-victim-impact-testimony" aria-label="Permalink to &quot;§ 19.2-295.3 Admission of victim impact testimony.&quot;">​</a></h1><p>Whether by trial or upon a plea of guilty, upon a finding that the defendant is guilty of a felony, the court shall permit the victim, as defined in § <a href="/vacode/19.2-11.01/">19.2-11.01</a>, upon motion of the attorney for the Commonwealth, to testify in the presence of the accused regarding the impact of the offense upon the victim. The court shall limit the victim&#39;s testimony to the factors set forth in clauses (i) through (vi) of subsection A of § <a href="/vacode/19.2-299.1/">19.2-299.1</a>. In the case of trial by jury and when the accused has requested the jury to ascertain punishment as provided in subsection A of § <a href="/vacode/19.2-295/">19.2-295</a>, the court shall permit the victim to testify at the sentencing hearing conducted pursuant to § <a href="/vacode/19.2-295.1/">19.2-295.1</a>. In all other cases of trial by jury, the case of trial by the court, or the case of a guilty plea, the court shall permit the victim to testify before the court prior to the imposition of the sentence by the presiding judge.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0485" target="_blank" rel="noreferrer">485</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0310" target="_blank" rel="noreferrer">310</a>; 2020, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0043" target="_blank" rel="noreferrer">43</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0344" target="_blank" rel="noreferrer">344</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0345" target="_blank" rel="noreferrer">345</a>.</p>',3),n=[r];function s(c,h,l,f,p,m){return t(),i("div",null,n)}const g=e(o,[["render",s]]);export{u as __pageData,g as default};
