import{_ as e,o as a,c as r,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 19.2-310.2:1 Saliva or tissue sample required for DNA analysis after arrest for a violent felony.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-310.2:1.md","filePath":"vacode/19.2-310.2:1.md"}'),i={name:"vacode/19.2-310.2:1.md"},o=t('<h1 id="§-19-2-310-2-1-saliva-or-tissue-sample-required-for-dna-analysis-after-arrest-for-a-violent-felony" tabindex="-1">§ 19.2-310.2:1 Saliva or tissue sample required for DNA analysis after arrest for a violent felony. <a class="header-anchor" href="#§-19-2-310-2-1-saliva-or-tissue-sample-required-for-dna-analysis-after-arrest-for-a-violent-felony" aria-label="Permalink to &quot;§ 19.2-310.2:1 Saliva or tissue sample required for DNA analysis after arrest for a violent felony.&quot;">​</a></h1><p>Every person arrested for the commission or attempted commission of a violent felony as defined in § <a href="/vacode/19.2-297.1/">19.2-297.1</a> or a violation or attempt to commit a violation of § <a href="/vacode/18.2-31/">18.2-31</a>, <a href="/vacode/18.2-89/">18.2-89</a>, <a href="/vacode/18.2-90/">18.2-90</a>, <a href="/vacode/18.2-91/">18.2-91</a>, or <a href="/vacode/18.2-92/">18.2-92</a>, shall have a sample of his saliva or tissue taken for DNA (deoxyribonucleic acid) analysis to determine identification characteristics specific to the person. After a determination by a magistrate or a grand jury that probable cause exists for the arrest, a sample shall be taken prior to the person&#39;s release from custody. The analysis shall be performed by the Department of Forensic Science or other entity designated by the Department. The identification characteristics of the profile resulting from the DNA analysis shall be stored and maintained by the Department in a DNA data bank and shall be made available as provided in § <a href="/vacode/19.2-310.5/">19.2-310.5</a>.</p><p>The clerk of the court shall notify the Department of final disposition of the criminal proceedings. If the charge for which the sample was taken is dismissed or the defendant is acquitted at trial, the Department shall destroy the sample and all records thereof, provided there is no other pending qualifying warrant or capias for an arrest or conviction that would otherwise require that the sample remain in the data bank.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0753" target="_blank" rel="noreferrer">753</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0773" target="_blank" rel="noreferrer">773</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0150" target="_blank" rel="noreferrer">150</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0445" target="_blank" rel="noreferrer">445</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0868" target="_blank" rel="noreferrer">868</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0182" target="_blank" rel="noreferrer">182</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0087" target="_blank" rel="noreferrer">87</a>.</p>',4),n=[o];function s(l,f,c,h,d,p){return a(),r("div",null,n)}const m=e(i,[["render",s]]);export{v as __pageData,m as default};
