import{_ as e,o,c as r,O as t}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 19.2-317 When writ of error lies in criminal case for accused; when for Commonwealth; when for county, city or town.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-317.md","filePath":"vacode/19.2-317.md"}'),a={name:"vacode/19.2-317.md"},n=t('<h1 id="§-19-2-317-when-writ-of-error-lies-in-criminal-case-for-accused-when-for-commonwealth-when-for-county-city-or-town" tabindex="-1">§ 19.2-317 When writ of error lies in criminal case for accused; when for Commonwealth; when for county, city or town. <a class="header-anchor" href="#§-19-2-317-when-writ-of-error-lies-in-criminal-case-for-accused-when-for-commonwealth-when-for-county-city-or-town" aria-label="Permalink to &quot;§ 19.2-317 When writ of error lies in criminal case for accused; when for Commonwealth; when for county, city or town.&quot;">​</a></h1><p>A. A writ of error shall lie in a criminal case to the judgment of a circuit court or the judge thereof, from the Court of Appeals as provided in § <a href="http://law.lis.virginia.gov/vacode/17.1-406/" target="_blank" rel="noreferrer">17.1-406</a>. It shall lie in any such case for the accused and if the case is for the violation of any law relating to the state revenue, it shall lie also for the Commonwealth.</p><p>B. A writ of error shall also lie for any county, city or town from the Supreme Court to the judgment of any circuit court declaring an ordinance of such county, city or town to be unconstitutional or otherwise invalid, except when the violation of any such ordinance is made a misdemeanor by state statute.</p><p>C. A writ of error shall also lie for the Commonwealth from the Supreme Court to a judgment of the Court of Appeals in a criminal case, except where the decision of the Court of Appeals is made final under § <a href="http://law.lis.virginia.gov/vacode/17.1-410/" target="_blank" rel="noreferrer">17.1-410</a> or § <a href="http://law.lis.virginia.gov/vacode/19.2-408/" target="_blank" rel="noreferrer">19.2-408</a>.</p><p>Code 1950, § 19.1-282; 1960, c. 366; 1975, c. 495; 1984, c. 703; 1997, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?971+ful+CHAP0358" target="_blank" rel="noreferrer">358</a>.</p>',5),i=[n];function c(l,s,h,f,d,u){return o(),r("div",null,i)}const p=e(a,[["render",c]]);export{w as __pageData,p as default};
