import{_ as e,o as a,c as r,O as o}from"./chunks/framework.9482e208.js";const w=JSON.parse('{"title":"§ 19.2-368.10 When awards to be made; reporting crime and cooperation with law enforcement.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-368.10.md","filePath":"vacode/19.2-368.10.md"}'),t={name:"vacode/19.2-368.10.md"},i=o('<h1 id="§-19-2-368-10-when-awards-to-be-made-reporting-crime-and-cooperation-with-law-enforcement" tabindex="-1">§ 19.2-368.10 When awards to be made; reporting crime and cooperation with law enforcement. <a class="header-anchor" href="#§-19-2-368-10-when-awards-to-be-made-reporting-crime-and-cooperation-with-law-enforcement" aria-label="Permalink to &quot;§ 19.2-368.10 When awards to be made; reporting crime and cooperation with law enforcement.&quot;">​</a></h1><p>No award shall be made unless the Commission finds that:</p><ol><li><p>A crime was committed;</p></li><li><p>Such crime directly resulted in an individual becoming a victim as defined in § <a href="/vacode/19.2-368.2/">19.2-368.2</a>, on whose behalf a claim is filed; and</p></li><li><p>Police records show that such crime was promptly reported to the proper authorities. In no case may an award be made where the police records show that such report was made more than 120 hours after the occurrence of such crime, unless the Commission, for good cause shown, finds the delay to have been justified. The provisions of this subdivision shall not apply to claims of sexual abuse.</p></li></ol><p>The Commission, upon finding that any claimant or award recipient has not fully cooperated with all law-enforcement agencies, may deny, reduce or withdraw any award, as the case may be.</p><p>1976, c. 605; 1977, c. 215; 1985, c. 446; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0855" target="_blank" rel="noreferrer">855</a>; 2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0683" target="_blank" rel="noreferrer">683</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0178" target="_blank" rel="noreferrer">178</a>.</p>',5),n=[i];function c(s,l,d,h,p,m){return a(),r("div",null,n)}const _=e(t,[["render",c]]);export{w as __pageData,_ as default};
