import{_ as e,o as a,c as t,O as i}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 19.2-38.1 Training standards; training prerequisite to reappointment; waiver.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-38.1.md","filePath":"vacode/19.2-38.1.md"}'),r={name:"vacode/19.2-38.1.md"},n=i('<h1 id="§-19-2-38-1-training-standards-training-prerequisite-to-reappointment-waiver" tabindex="-1">§ 19.2-38.1 Training standards; training prerequisite to reappointment; waiver. <a class="header-anchor" href="#§-19-2-38-1-training-standards-training-prerequisite-to-reappointment-waiver" aria-label="Permalink to &quot;§ 19.2-38.1 Training standards; training prerequisite to reappointment; waiver.&quot;">​</a></h1><p>The Committee on District Courts shall establish minimum training and certification standards for magistrates in accordance with such rules and regulations as may be established by the Committee. Every magistrate shall comply with these standards and shall complete the minimum training standards as a prerequisite for continuing to serve as magistrate beyond the nine-month probationary period as established by § <a href="http://law.lis.virginia.gov/vacode/19.2-38/" target="_blank" rel="noreferrer">19.2-38</a>. The Committee on District Courts upon request may waive any portion of the minimum training standards for an individual magistrate.</p><p>1980, c. 505; 1985, c. 132; 1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0611" target="_blank" rel="noreferrer">611</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0551" target="_blank" rel="noreferrer">551</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0691" target="_blank" rel="noreferrer">691</a>.</p>',3),s=[n];function o(d,l,c,p,m,g){return a(),t("div",null,s)}const u=e(r,[["render",o]]);export{_ as __pageData,u as default};
