import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 19.2-386.21 Forfeiture of counterfeit and contraband cigarettes.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-386.21.md","filePath":"vacode/19.2-386.21.md"}'),o={name:"vacode/19.2-386.21.md"},i=r('<h1 id="§-19-2-386-21-forfeiture-of-counterfeit-and-contraband-cigarettes" tabindex="-1">§ 19.2-386.21 Forfeiture of counterfeit and contraband cigarettes. <a class="header-anchor" href="#§-19-2-386-21-forfeiture-of-counterfeit-and-contraband-cigarettes" aria-label="Permalink to &quot;§ 19.2-386.21 Forfeiture of counterfeit and contraband cigarettes.&quot;">​</a></h1><p>Counterfeit cigarettes possessed in violation of § <a href="/vacode/18.2-246.14/">18.2-246.14</a> and cigarettes possessed in violation of § <a href="/vacode/58.1-1017/">58.1-1017</a> or <a href="/vacode/58.1-1017.1/">58.1-1017.1</a> shall be subject to seizure, forfeiture, and destruction or court-ordered assignment for use by a law-enforcement undercover operation by the Virginia Alcoholic Beverage Control Authority or any law-enforcement officer of the Commonwealth. However, any undercover operation that makes use of counterfeit cigarettes shall ensure that the counterfeit cigarettes remain under the control and command of law enforcement and shall not be distributed to a member of the general public who is not the subject of a criminal investigation. All fixtures, equipment, materials, and personal property used in substantial connection with (i) the sale or possession of counterfeit cigarettes in a knowing and intentional violation of Article 10 (§ <a href="/vacode/18.2-246.6/">18.2-246.6</a> et seq.) of Chapter 6 of Title 18.2 or (ii) the sale or possession of cigarettes in a knowing and intentional violation of § <a href="/vacode/58.1-1017/">58.1-1017</a> or <a href="/vacode/58.1-1017.1/">58.1-1017.1</a> shall be subject to seizure and forfeiture according to the procedures contained in Chapter 22.1 (§ <a href="/vacode/19.2-386.1/">19.2-386.1</a> et seq.), applied mutatis mutandis.</p><p>2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0627" target="_blank" rel="noreferrer">627</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0422" target="_blank" rel="noreferrer">422</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0458" target="_blank" rel="noreferrer">458</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0038" target="_blank" rel="noreferrer">38</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0730" target="_blank" rel="noreferrer">730</a>.</p>',3),n=[i];function c(s,f,l,d,h,u){return t(),a("div",null,n)}const _=e(o,[["render",c]]);export{p as __pageData,_ as default};
