import{_ as e,o as r,c as t,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 19.2-386.4 Records and handling of seized property.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-386.4.md","filePath":"vacode/19.2-386.4.md"}'),o={name:"vacode/19.2-386.4.md"},a=i('<h1 id="§-19-2-386-4-records-and-handling-of-seized-property" tabindex="-1">§ 19.2-386.4 Records and handling of seized property. <a class="header-anchor" href="#§-19-2-386-4-records-and-handling-of-seized-property" aria-label="Permalink to &quot;§ 19.2-386.4 Records and handling of seized property.&quot;">​</a></h1><p>Any agency seizing property under § <a href="http://law.lis.virginia.gov/vacode/19.2-386.2/" target="_blank" rel="noreferrer">19.2-386.2</a>, Chapter 22.2 (§ <a href="http://law.lis.virginia.gov/vacode/19.2-386.15/" target="_blank" rel="noreferrer">19.2-386.15</a> et seq.), or other provision under the Code, pending forfeiture and final disposition, may do any of the following:</p><ol><li><p>Place the property under constructive seizure by posting notice of seizure for forfeiture on the property or by filing notice of seizure for forfeiture in any appropriate public record relating to property;</p></li><li><p>Remove the property to a storage area for safekeeping or, if the property is a negotiable instrument or money, deposit it in an interest-bearing account;</p></li><li><p>Remove the property to a place designated by the circuit court in the county or city wherein the property was seized; or</p></li><li><p>Provide for another custodian or agency to take custody of the property and remove it to an appropriate location within or without the jurisdiction of the circuit court in the county or city wherein the property was seized or in which the complaint was filed.</p></li></ol><p>A report regarding the type of property subject to forfeiture and its handling pursuant to this section and § <a href="http://law.lis.virginia.gov/vacode/19.2-386.5/" target="_blank" rel="noreferrer">19.2-386.5</a>, and the final disposition of the property shall be filed by the seizing agency with the Department of Criminal Justice Services in accordance with regulations promulgated by the Board.</p><p>1989, c. 690; 1991, c. 560; 2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0588" target="_blank" rel="noreferrer">588</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0623" target="_blank" rel="noreferrer">623</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0995" target="_blank" rel="noreferrer">995</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0283" target="_blank" rel="noreferrer">283</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0756" target="_blank" rel="noreferrer">756</a>.</p>',5),n=[a];function p(l,c,s,d,h,f){return r(),t("div",null,n)}const _=e(o,[["render",p]]);export{u as __pageData,_ as default};
