import{_ as e,o as t,c as i,O as a}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 19.2-387.3 Substantial Risk Order Registry; maintenance; access.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-387.3.md","filePath":"vacode/19.2-387.3.md"}'),r={name:"vacode/19.2-387.3.md"},n=a('<h1 id="§-19-2-387-3-substantial-risk-order-registry-maintenance-access" tabindex="-1">§ 19.2-387.3 Substantial Risk Order Registry; maintenance; access. <a class="header-anchor" href="#§-19-2-387-3-substantial-risk-order-registry-maintenance-access" aria-label="Permalink to &quot;§ 19.2-387.3 Substantial Risk Order Registry; maintenance; access.&quot;">​</a></h1><p>A. The Department of State Police shall keep and maintain a computerized Substantial Risk Order Registry (the Registry) for the entry of orders issued pursuant to § <a href="/vacode/19.2-152.13/">19.2-152.13</a> or <a href="/vacode/19.2-152.14/">19.2-152.14</a>. The Department of State Police shall make the Registry information available, upon request, to criminal justice agencies, including local law-enforcement agencies, through the Virginia Criminal Information Network. The Department of State Police may make the Registry information available upon request to institutions of higher education and other research organizations or institutions in the Commonwealth. The Department of State Police shall remove the names and other personal identifying information from the data before it is released to the institution of higher education or research organization or other institution. Registry information provided under this section shall be used only for the purposes of the administration of criminal justice as defined in § <a href="/vacode/9.1-101/">9.1-101</a>, except as otherwise provided in this subsection.</p><p>B. No liability shall be imposed upon any law-enforcement official who disseminates information or fails to disseminate information in good faith compliance with the requirements of this section, but this provision shall not be construed to grant immunity for gross negligence or willful misconduct.</p><p>2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0887" target="_blank" rel="noreferrer">887</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0888" target="_blank" rel="noreferrer">888</a>; 2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0461" target="_blank" rel="noreferrer">461</a>.</p>',4),o=[n];function s(c,l,h,d,f,m){return t(),i("div",null,o)}const g=e(r,[["render",s]]);export{p as __pageData,g as default};
