import{_ as i,o,c as a,z as t,a as e}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 19.2-389.2 Background checks of applicants of the Metropolitan Washington Airports Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-389.2.md","filePath":"vacode/19.2-389.2.md"}'),n={name:"vacode/19.2-389.2.md"},r=t("h1",{id:"§-19-2-389-2-background-checks-of-applicants-of-the-metropolitan-washington-airports-authority",tabindex:"-1"},[e("§ 19.2-389.2 Background checks of applicants of the Metropolitan Washington Airports Authority. "),t("a",{class:"header-anchor",href:"#§-19-2-389-2-background-checks-of-applicants-of-the-metropolitan-washington-airports-authority","aria-label":'Permalink to "§ 19.2-389.2 Background checks of applicants of the Metropolitan Washington Airports Authority."'},"​")],-1),h=t("p",null,[e("The police department of the Metropolitan Washington Airports Authority as established in Chapter 10 (§ "),t("a",{href:"http://law.lis.virginia.gov/vacode/5.1-152/",target:"_blank",rel:"noreferrer"},"5.1-152"),e(" et seq.) of Title 5.1 may require an applicant, upon conditional offer of employment with the Authority, to submit to fingerprinting and to provide personal descriptive information to be forwarded along with the applicant's fingerprints through the Central Criminal Records Exchange and the Federal Bureau of Investigation for the purpose of obtaining criminal history record information regarding such applicant.")],-1),s=t("p",null,"The Central Criminal Records Exchange, upon receipt of an applicant's record or notification that no record exists, shall make a report to the chief of the police department of the Authority or his designee, provided the designee is an employee of the police department of the Authority. In determining whether a criminal conviction directly relates to a position, the Authority shall consider the following criteria: (i) the nature and seriousness of the crime; (ii) the relationship of the crime to the work to be performed in the position applied for; (iii) the extent to which the position applied for might offer an opportunity to engage in further criminal activity of the same type as that in which the applicant had been involved; (iv) the relationship of the crime to the ability, capacity, or fitness required to perform the duties and discharge the responsibilities of the position being sought; (v) the extent and nature of the applicant's past criminal activity; (vi) the age of the applicant at the time of the commission of the crime; (vii) the amount of time that has elapsed since the applicant's last involvement in the commission of a crime; (viii) the conduct and work activity of the applicant prior to and following the criminal activity; and (ix) evidence of the applicant's rehabilitation or rehabilitative effort while incarcerated or following release.",-1),c=t("p",null,"If an applicant is denied employment because of information appearing in his criminal history record, the Authority shall notify the applicant that information obtained from the Central Criminal Records Exchange contributed to such denial. The criminal history record information obtained pursuant to this section shall be used solely to determine an applicant's eligibility for employment by the Authority and access to restricted areas of Ronald Reagan Washington National Airport and Washington Dulles International Airport in compliance with 49 U.S.C. § 44936 and shall otherwise be confidential.",-1),l=t("p",null,[e("2014, c. "),t("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0057",target:"_blank",rel:"noreferrer"},"57"),e(".")],-1),p=[r,h,s,c,l];function d(f,m,u,g,y,v){return o(),a("div",null,p)}const k=i(n,[["render",d]]);export{b as __pageData,k as default};
