import{_ as e,o as a,c as t,O as o}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 19.2-391 Records to be made available to Exchange by state officials and agencies; duplication of records.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-391.md","filePath":"vacode/19.2-391.md"}'),i={name:"vacode/19.2-391.md"},r=o('<h1 id="§-19-2-391-records-to-be-made-available-to-exchange-by-state-officials-and-agencies-duplication-of-records" tabindex="-1">§ 19.2-391 Records to be made available to Exchange by state officials and agencies; duplication of records. <a class="header-anchor" href="#§-19-2-391-records-to-be-made-available-to-exchange-by-state-officials-and-agencies-duplication-of-records" aria-label="Permalink to &quot;§ 19.2-391 Records to be made available to Exchange by state officials and agencies; duplication of records.&quot;">​</a></h1><p>Each state official and agency shall make available to the Central Criminal Records Exchange such of their records as are pertinent to its functions and shall cooperate with the Exchange in the development and use of equipment and facilities on a joint basis, where feasible. No state official or agency shall maintain records which are a duplication of the records on deposit in the Central Criminal Records Exchange, except to the extent necessary for efficient internal administration of such agency. Furthermore, the Virginia Parole Board may receive and use electronically disseminated criminal history record information from the Central Criminal Records Exchange as required to make parole determinations pursuant to subdivisions 1, 2, 3, 4, and 6 of § <a href="/vacode/53.1-136/">53.1-136</a>, provided the data is (i) temporarily stored with the Board solely for operational purposes, (ii) purged within 30 days of receipt of updated data by the Board, and (iii) accessed and viewed solely by Parole Board members and authorized staff pursuant to §§ <a href="/vacode/9.1-101/">9.1-101</a> and <a href="/vacode/9.1-130/">9.1-130</a>.</p><p>Code 1950, § 19.1-19.4; 1966, c. 669; 1975, c. 495; 1993, c. 313; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0529" target="_blank" rel="noreferrer">529</a>.</p>',3),n=[r];function c(s,d,l,f,h,p){return a(),t("div",null,n)}const _=e(i,[["render",c]]);export{u as __pageData,_ as default};
