import{_ as e,o,c as a,O as t}from"./chunks/framework.9482e208.js";const p=JSON.parse('{"title":"§ 19.2-392.6 (For contingent effective date see Acts 2021, Sp. Sess. I, cc. 524 and 542) Automatic sealing of offenses resulting in a deferred and dismissed disposition or conviction.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-392.6.md","filePath":"vacode/19.2-392.6.md"}'),i={name:"vacode/19.2-392.6.md"},n=t('<h1 id="§-19-2-392-6-for-contingent-effective-date-see-acts-2021-sp-sess-i-cc-524-and-542-automatic-sealing-of-offenses-resulting-in-a-deferred-and-dismissed-disposition-or-conviction" tabindex="-1">§ 19.2-392.6 (For contingent effective date see Acts 2021, Sp. Sess. I, cc. 524 and 542) Automatic sealing of offenses resulting in a deferred and dismissed disposition or conviction. <a class="header-anchor" href="#§-19-2-392-6-for-contingent-effective-date-see-acts-2021-sp-sess-i-cc-524-and-542-automatic-sealing-of-offenses-resulting-in-a-deferred-and-dismissed-disposition-or-conviction" aria-label="Permalink to &quot;§ 19.2-392.6 (For contingent effective date see Acts 2021, Sp. Sess. I, cc. 524 and 542) Automatic sealing of offenses resulting in a deferred and dismissed disposition or conviction.&quot;">​</a></h1><p>A. If a person was charged with an offense in violation of § <a href="/vacode/4.1-305/">4.1-305</a> or former § <a href="/vacode/18.2-250.1/">18.2-250.1</a>, and such offense was deferred and dismissed as provided in § <a href="/vacode/4.1-305/">4.1-305</a> or <a href="/vacode/18.2-251/">18.2-251</a>, such offense, including any records relating to such offense, shall be ordered to be automatically sealed in the manner set forth in § <a href="/vacode/19.2-392.7/">19.2-392.7</a>, subject to the provisions of subsections C and D.</p><p>B. If a person was convicted of a violation of any of the following sections, such conviction, including any records relating to such conviction, shall be ordered to be automatically sealed in the manner set forth in § <a href="/vacode/19.2-392.7/">19.2-392.7</a>, subject to the provisions of subsections C and D: § <a href="/vacode/4.1-305/">4.1-305</a>, <a href="/vacode/18.2-96/">18.2-96</a>, <a href="/vacode/18.2-103/">18.2-103</a>, <a href="/vacode/18.2-119/">18.2-119</a>, <a href="/vacode/18.2-120/">18.2-120</a>, or <a href="/vacode/18.2-134/">18.2-134</a>; a misdemeanor violation of § <a href="/vacode/18.2-248.1/">18.2-248.1</a>; or former § <a href="/vacode/18.2-250.1/">18.2-250.1</a> or <a href="/vacode/18.2-415/">18.2-415</a>.</p><p>C. Subject to the provisions of subsection D, any offense listed under subsection A and any conviction listed under subsection B shall be ordered to be automatically sealed if seven years have passed since the date of the dismissal or conviction and the person charged with or convicted of such offense has not been convicted of violating any law of the Commonwealth that requires a report to the Central Criminal Records Exchange under subsection A of § <a href="/vacode/19.2-390/">19.2-390</a> or any other state, the District of Columbia, or the United States or any territory thereof, excluding traffic infractions under Title 46.2, during that time period.</p><p>D. No offense listed under subsection A shall be automatically sealed if, on the date of the deferral or dismissal, the person was convicted of another offense that is not eligible for automatic sealing under subsection A or B. No conviction listed under subsection B shall be automatically sealed if, on the date of the conviction, the person was convicted of another offense that is not eligible for automatic sealing under subsection A or B.</p><p>E. This section shall not be construed as prohibiting a person from seeking sealing in the circuit court pursuant to the provisions of § <a href="/vacode/19.2-392.12/">19.2-392.12</a>.</p><p>2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0524" target="_blank" rel="noreferrer">524</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0542" target="_blank" rel="noreferrer">542</a>.</p>',7),s=[n];function r(c,d,f,l,h,u){return o(),a("div",null,s)}const g=e(i,[["render",r]]);export{p as __pageData,g as default};
