import{_ as t,o,c as i,z as e,a as n}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 19.2-393 Definitions.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-393.md","filePath":"vacode/19.2-393.md"}'),a={name:"vacode/19.2-393.md"},r=e("h1",{id:"§-19-2-393-definitions",tabindex:"-1"},[n("§ 19.2-393 Definitions. "),e("a",{class:"header-anchor",href:"#§-19-2-393-definitions","aria-label":'Permalink to "§ 19.2-393 Definitions."'},"​")],-1),s=e("p",null,'An "inspection warrant" is an order in writing, made in the name of the Commonwealth, signed by any judge of the circuit court whose territorial jurisdiction encompasses the property or premises to be inspected or entered, and directed to a state or local official, commanding him to enter and to conduct any inspection, testing or collection of samples for testing required or authorized by state or local law or regulation in connection with the manufacturing, emitting or presence of a toxic substance, and which describes, either directly or by reference to any accompanying or attached supporting affidavit, the property or premises where the inspection, testing or collection of samples for testing is to occur. Such warrant shall be sufficiently accurate in description so that the official executing the warrant and the owner or custodian of the property or premises can reasonably determine from the warrant the activity, condition, circumstance, object or property of which inspection, testing or collection of samples for testing is authorized.',-1),c=e("p",null,'For the purposes of this chapter, "manufacturing" means producing, formulating, packaging, or diluting any substance for commercial sale or resale; "emitting" means the release of any substance, whether or not intentional or avoidable, into the work environment, into the air, into the water, or otherwise into the human environment; and "toxic substance" means any substance, including (i) any raw material, intermediate product, catalyst, final product and by-product of any operation conducted in a commercial establishment and (ii) any biological organism, that has the capacity, through its physical, chemical, or biological properties, to pose a substantial risk to humans, aquatic organisms or any other animal of illness, death or impairment of normal functions, either immediately or over a period of time.',-1),l=e("p",null,"1976, c. 625; 1979, c. 122.",-1),d=[r,s,c,l];function h(m,p,u,f,g,y){return o(),i("div",null,d)}const w=t(a,[["render",h]]);export{b as __pageData,w as default};
