import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 19.2-46.2 Full-time magistrates; certification for retirement coverage.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-46.2.md","filePath":"vacode/19.2-46.2.md"}'),i={name:"vacode/19.2-46.2.md"},o=r('<h1 id="§-19-2-46-2-full-time-magistrates-certification-for-retirement-coverage" tabindex="-1">§ 19.2-46.2 Full-time magistrates; certification for retirement coverage. <a class="header-anchor" href="#§-19-2-46-2-full-time-magistrates-certification-for-retirement-coverage" aria-label="Permalink to &quot;§ 19.2-46.2 Full-time magistrates; certification for retirement coverage.&quot;">​</a></h1><p>The Committee on District Courts shall certify to the director of the Virginia Retirement System the names of those magistrates serving on a regular full-time basis. Certification by the Committee shall qualify a magistrate as a state employee, for purposes of §§ <a href="http://law.lis.virginia.gov/vacode/51.1-124.3/" target="_blank" rel="noreferrer">51.1-124.3</a> and <a href="http://law.lis.virginia.gov/vacode/51.1-152/" target="_blank" rel="noreferrer">51.1-152</a> of the Virginia Retirement System (§ <a href="http://law.lis.virginia.gov/vacode/51.1-124.1/" target="_blank" rel="noreferrer">51.1-124.1</a> et seq.), effective on the date given in the certificate as the date on which such magistrate first served on a regular full-time basis on or after January 1, 1974.</p><p>1974, c. 353, § 14.1-44.2:1; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0872" target="_blank" rel="noreferrer">872</a>.</p>',3),s=[o];function n(c,l,f,m,g,h){return t(),a("div",null,s)}const v=e(i,[["render",n]]);export{d as __pageData,v as default};
