import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 19.2-81.6 Authority of law-enforcement officers to arrest illegal aliens.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-81.6.md","filePath":"vacode/19.2-81.6.md"}'),i={name:"vacode/19.2-81.6.md"},o=r('<h1 id="§-19-2-81-6-authority-of-law-enforcement-officers-to-arrest-illegal-aliens" tabindex="-1">§ 19.2-81.6 Authority of law-enforcement officers to arrest illegal aliens. <a class="header-anchor" href="#§-19-2-81-6-authority-of-law-enforcement-officers-to-arrest-illegal-aliens" aria-label="Permalink to &quot;§ 19.2-81.6 Authority of law-enforcement officers to arrest illegal aliens.&quot;">​</a></h1><p>All law-enforcement officers enumerated in § <a href="http://law.lis.virginia.gov/vacode/19.2-81/" target="_blank" rel="noreferrer">19.2-81</a> shall have the authority to enforce immigration laws of the United States, pursuant to the provisions of this section. Any law-enforcement officer enumerated in § <a href="http://law.lis.virginia.gov/vacode/19.2-81/" target="_blank" rel="noreferrer">19.2-81</a> may, in the course of acting upon reasonable suspicion that an individual has committed or is committing a crime, arrest the individual without a warrant upon receiving confirmation from the Bureau of Immigration and Customs Enforcement of the United States Department of Homeland Security that the individual (i) is an alien illegally present in the United States, and (ii) has previously been convicted of a felony in the United States and deported or left the United States after such conviction. Upon receiving such confirmation, the officer shall take the individual forthwith before a magistrate or other issuing authority and proceed pursuant to § <a href="http://law.lis.virginia.gov/vacode/19.2-82/" target="_blank" rel="noreferrer">19.2-82</a>.</p><p>2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0360" target="_blank" rel="noreferrer">360</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0412" target="_blank" rel="noreferrer">412</a>.</p>',3),n=[o];function l(s,c,f,h,d,u){return t(),a("div",null,n)}const g=e(i,[["render",l]]);export{_ as __pageData,g as default};
