import{_ as e,o,c as t,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 19.2-83.5 Use of deadly force by a law-enforcement officer during an arrest or detention.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-83.5.md","filePath":"vacode/19.2-83.5.md"}'),a={name:"vacode/19.2-83.5.md"},n=r('<h1 id="§-19-2-83-5-use-of-deadly-force-by-a-law-enforcement-officer-during-an-arrest-or-detention" tabindex="-1">§ 19.2-83.5 Use of deadly force by a law-enforcement officer during an arrest or detention. <a class="header-anchor" href="#§-19-2-83-5-use-of-deadly-force-by-a-law-enforcement-officer-during-an-arrest-or-detention" aria-label="Permalink to &quot;§ 19.2-83.5 Use of deadly force by a law-enforcement officer during an arrest or detention.&quot;">​</a></h1><p>A. A law-enforcement officer shall not use deadly force against a person unless:</p><ol><li><p>The law-enforcement officer reasonably believes that deadly force is immediately necessary to protect the law-enforcement officer or another person, other than the subject of the use of deadly force, from the threat of serious bodily injury or death;</p></li><li><p>If feasible, the law-enforcement officer has provided a warning to the subject of the deadly force that he will use deadly force;</p></li><li><p>The law-enforcement officer&#39;s actions are reasonable, given the totality of the circumstances; and</p></li><li><p>All other options have been exhausted or do not reasonably lend themselves to the circumstances.</p></li></ol><p>B. In determining if a law-enforcement officer&#39;s use of deadly force is proper, the following factors shall be considered:</p><ol><li><p>The reasonableness of the law-enforcement officer&#39;s belief and actions from the perspective of a reasonable law-enforcement officer on the scene at the time of the incident; and</p></li><li><p>The totality of the circumstances, including (i) the amount of time available to the law-enforcement officer to make a decision; (ii) whether the subject of the use of deadly force (a) possessed or appeared to possess a deadly weapon and (b) refused to comply with the law-enforcement officer&#39;s lawful order to surrender an object believed to be a deadly weapon prior to the law-enforcement officer using deadly force; (iii) whether the law-enforcement officer engaged in de-escalation measures prior to the use of deadly force, including taking cover, waiting for backup, trying to calm the subject prior to the use of force, or using non-deadly force prior to the use of deadly force; (iv) whether any conduct by the law-enforcement officer prior to the use of deadly force intentionally increased the risk of a confrontation resulting in deadly force being used; and (v) the seriousness of the suspected crime.</p></li></ol><p>2020, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?202+ful+CHAP0037" target="_blank" rel="noreferrer">37</a>.</p>',6),i=[n];function f(c,s,l,d,h,p){return o(),t("div",null,i)}const y=e(a,[["render",f]]);export{m as __pageData,y as default};
