import{_ as e,o as t,c as i,O as o}from"./chunks/framework.9482e208.js";const d=JSON.parse('{"title":"§ 19.2-9.1 Written notice required for complaining witness who is requested to take polygraph test.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/19.2-9.1.md","filePath":"vacode/19.2-9.1.md"}'),n={name:"vacode/19.2-9.1.md"},a=o('<h1 id="§-19-2-9-1-written-notice-required-for-complaining-witness-who-is-requested-to-take-polygraph-test" tabindex="-1">§ 19.2-9.1 Written notice required for complaining witness who is requested to take polygraph test. <a class="header-anchor" href="#§-19-2-9-1-written-notice-required-for-complaining-witness-who-is-requested-to-take-polygraph-test" aria-label="Permalink to &quot;§ 19.2-9.1 Written notice required for complaining witness who is requested to take polygraph test.&quot;">​</a></h1><p>A. For offenses not specified in subsection B, if a complaining witness is requested to submit to a polygraph examination during the course of a criminal investigation, such witness shall be informed in writing prior to the examination that (i) the examination is voluntary, (ii) the results thereof are inadmissible as evidence and (iii) the agreement of the complaining witness to submit thereto shall not be the sole condition for initiating or continuing the criminal investigation.</p><p>B. No law-enforcement officer, attorney for the Commonwealth, or other government official shall ask or require a victim of an alleged sex offense to submit to a polygraph examination or other truth-telling device as a condition for proceeding with the investigation of such an offense. If a victim is requested to submit to a polygraph examination during the course of a criminal investigation, such victim shall be informed in writing of the provisions of subsection A and that the refusal of a victim to submit to such an examination shall not prevent the investigation, charging, or prosecution of the offense.</p><p>C. A &quot;sex offense,&quot; for the purposes of this section, shall mean any offense set forth in Article 7 (§ <a href="http://law.lis.virginia.gov/vacode/18.2-61/" target="_blank" rel="noreferrer">18.2-61</a> et seq.) of Chapter 4 of Title 18.2.</p><p>1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0336" target="_blank" rel="noreferrer">336</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0512" target="_blank" rel="noreferrer">512</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0748" target="_blank" rel="noreferrer">748</a>.</p>',5),r=[a];function s(l,c,h,f,p,g){return t(),i("div",null,r)}const m=e(n,[["render",s]]);export{d as __pageData,m as default};
