import{_ as i,o as e,c as t,O as s}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-1118 Purchases from employment services organizations of Virginia serving individuals with disabilities.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1118.md","filePath":"vacode/2.2-1118.md"}'),a={name:"vacode/2.2-1118.md"},n=s('<h1 id="§-2-2-1118-purchases-from-employment-services-organizations-of-virginia-serving-individuals-with-disabilities" tabindex="-1">§ 2.2-1118 Purchases from employment services organizations of Virginia serving individuals with disabilities. <a class="header-anchor" href="#§-2-2-1118-purchases-from-employment-services-organizations-of-virginia-serving-individuals-with-disabilities" aria-label="Permalink to &quot;§ 2.2-1118 Purchases from employment services organizations of Virginia serving individuals with disabilities.&quot;">​</a></h1><p>A. The Division shall publish annually a list of materials, supplies, services and equipment which, in the opinion of the Division, would be beneficial to the Commonwealth to procure from an employment services organization as defined in § <a href="http://law.lis.virginia.gov/vacode/2.2-4301/" target="_blank" rel="noreferrer">2.2-4301</a>. The list shall exclude items currently produced by schools or workshops under the supervision of the Virginia Department for the Blind and Vision Impaired or by inmates confined in state correctional institutions.</p><p>B. Any item or service included on the list required by subsection A may be purchased by the Division from employment services organizations serving individuals with disabilities without competitive procurement, if the Division is satisfied that the items and services (i) can be purchased within ten percent of their fair market value, (ii) will be of acceptable quality, and (iii) can be produced in sufficient quantities within the time required.</p><p>C. Nothing in this section shall prohibit the Division from amending the list required under subsection A by adding categories to the list after it has been published.</p><p>1978, c. 24, § 2.1-450.1; 1982, c. 647; 1987, c. 72; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2012, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0632" target="_blank" rel="noreferrer">632</a>.</p>',5),r=[n];function o(l,c,d,h,p,u){return e(),t("div",null,r)}const v=i(a,[["render",o]]);export{f as __pageData,v as default};
