import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-1119 Cases in which purchasing through Division not mandatory.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1119.md","filePath":"vacode/2.2-1119.md"}'),n={name:"vacode/2.2-1119.md"},t=r('<h1 id="§-2-2-1119-cases-in-which-purchasing-through-division-not-mandatory" tabindex="-1">§ 2.2-1119 Cases in which purchasing through Division not mandatory. <a class="header-anchor" href="#§-2-2-1119-cases-in-which-purchasing-through-division-not-mandatory" aria-label="Permalink to &quot;§ 2.2-1119 Cases in which purchasing through Division not mandatory.&quot;">​</a></h1><p>A. Unless otherwise ordered by the Governor, the purchasing of materials, equipment, supplies, and nonprofessional services through the Division shall not be mandatory in the following cases:</p><ol><li><p>Materials, equipment and supplies incident to the performance of a contract for labor or for labor and materials;</p></li><li><p>Manuscripts, maps, audiovisual materials, books, pamphlets and periodicals purchased for the use of The Library of Virginia or any other library in the Commonwealth supported in whole or in part by state funds;</p></li><li><p>Perishable articles, provided that no article except fresh vegetables, fish, eggs or milk shall be considered perishable within the meaning of this subdivision, unless so classified by the Division;</p></li><li><p>Materials, equipment and supplies needed by the Commonwealth Transportation Board; however, this exception may include, office stationery and supplies, office equipment, janitorial equipment and supplies, and coal and fuel oil for heating purposes shall not be included except when authorized in writing by the Division;</p></li><li><p>Materials, equipment, and supplies needed by the Virginia Alcoholic Beverage Control Authority, including office stationery and supplies, office equipment, and janitorial equipment and supplies; however, coal and fuel oil for heating purposes shall not be included except when authorized in writing by the Division;</p></li><li><p>Binding and rebinding of the books and other literary materials of libraries operated by the Commonwealth or under its authority;</p></li><li><p>Printing of the records of the Supreme Court; and</p></li><li><p>Financial services, including without limitation, underwriters, financial advisors, investment advisors and banking services.</p></li></ol><p>B. Telecommunications and information technology goods and services of every description shall be procured as provided by § <a href="/vacode/2.2-2012/">2.2-2012</a>.</p><p>Code 1950, § 2-260; 1958, c. 124; 1966, c. 677, § 2.1-286; 1970, c. 225; 1977, c. 672, § 2.1-451; 1982, c. 647; 1988, c. 140; 1994, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?941+ful+CHAP0064" target="_blank" rel="noreferrer">64</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0895" target="_blank" rel="noreferrer">895</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>; 2015, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0038" target="_blank" rel="noreferrer">38</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0730" target="_blank" rel="noreferrer">730</a>.</p>',5),o=[t];function s(l,h,p,c,d,u){return i(),a("div",null,o)}const m=e(n,[["render",s]]);export{f as __pageData,m as default};
