import{_ as a,o as r,c as o,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-1146 Department may lease certain state property; approval of leases by Attorney General; disposition of rentals.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1146.md","filePath":"vacode/2.2-1146.md"}'),n={name:"vacode/2.2-1146.md"},s=e("h1",{id:"§-2-2-1146-department-may-lease-certain-state-property-approval-of-leases-by-attorney-general-disposition-of-rentals",tabindex:"-1"},[t("§ 2.2-1146 Department may lease certain state property; approval of leases by Attorney General; disposition of rentals. "),e("a",{class:"header-anchor",href:"#§-2-2-1146-department-may-lease-certain-state-property-approval-of-leases-by-attorney-general-disposition-of-rentals","aria-label":'Permalink to "§ 2.2-1146 Department may lease certain state property; approval of leases by Attorney General; disposition of rentals."'},"​")],-1),l=e("p",null,"The Department, with the written approval of the Governor, may lease land, buildings and any portions thereof owned by the Commonwealth and under the control of the Department, when such land, buildings, or portions thereof are in excess of current and foreseeable needs of the Department. All such leases shall be executed in the name of the Commonwealth and shall be in a form approved by the Attorney General. The leases may run for such time as may be approved by the Governor and shall be for appropriate rental. All rentals received shall be retained by the Department and used for paying the costs of entering and administering such leases and for off-setting the costs of maintaining and operating the facilities under control of the Department. Notwithstanding any law to the contrary or how title to the property was acquired, the deed or lease shall be executed on behalf of the Commonwealth by the Director of the Department or his designee, and such action shall not create a cloud on the title to the property. The terms of the lease shall be subject to the written approval of the Governor or his designee.",-1),i=e("p",null,[t("1962, c. 262, § 2-66.2; 1966, cc. 55, 677, § 2.1-84; 1977, c. 672, § 2.1-503; 2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844",target:"_blank",rel:"noreferrer"},"844"),t("; 2009, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?091+ful+CHAP0612",target:"_blank",rel:"noreferrer"},"612"),t(".")],-1),p=[s,l,i];function h(d,c,f,m,y,b){return r(),o("div",null,p)}const _=a(n,[["render",h]]);export{g as __pageData,_ as default};
