import{_ as n,o as r,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-115.1 COVID-19 Relief Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-115.1.md","filePath":"vacode/2.2-115.1.md"}'),o={name:"vacode/2.2-115.1.md"},s=e("h1",{id:"§-2-2-115-1-covid-19-relief-fund",tabindex:"-1"},[t("§ 2.2-115.1 COVID-19 Relief Fund. "),e("a",{class:"header-anchor",href:"#§-2-2-115-1-covid-19-relief-fund","aria-label":'Permalink to "§ 2.2-115.1 COVID-19 Relief Fund."'},"​")],-1),i=e("p",null,`There is hereby created in the state treasury a special nonreverting fund to be known as the COVID-19 Relief Fund, referred to in this section as "the Fund." The Fund shall be established on the books of the Comptroller. All funds appropriated to the Fund and any gifts, donations, grants, bequests, and other funds received on its behalf shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used by the Governor solely for the purposes of responding to the Commonwealth's needs related to the Coronavirus Disease of 2019 (COVID-19) pandemic. Expenditures and disbursements from the Fund shall be made by the State Treasurer on warrants issued by the Comptroller upon written request signed by the Governor or his designee.`,-1),d=e("p",null,[t("2020, cc. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1217",target:"_blank",rel:"noreferrer"},"1217"),t(", "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1277",target:"_blank",rel:"noreferrer"},"1277"),t(".")],-1),l=[s,i,d];function h(u,c,f,p,_,b){return r(),a("div",null,l)}const v=n(o,[["render",h]]);export{g as __pageData,v as default};
