import{_ as e,o as i,c as t,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-1165 Inspection of public school buildings; certification of inspection; certain inspections made before July 1, 1986, deemed in compliance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1165.md","filePath":"vacode/2.2-1165.md"}'),o={name:"vacode/2.2-1165.md"},c=n('<h1 id="§-2-2-1165-inspection-of-public-school-buildings-certification-of-inspection-certain-inspections-made-before-july-1-1986-deemed-in-compliance" tabindex="-1">§ 2.2-1165 Inspection of public school buildings; certification of inspection; certain inspections made before July 1, 1986, deemed in compliance. <a class="header-anchor" href="#§-2-2-1165-inspection-of-public-school-buildings-certification-of-inspection-certain-inspections-made-before-july-1-1986-deemed-in-compliance" aria-label="Permalink to &quot;§ 2.2-1165 Inspection of public school buildings; certification of inspection; certain inspections made before July 1, 1986, deemed in compliance.&quot;">​</a></h1><p>A. Every public school division in the Commonwealth shall ensure that every school building owned or operated by it, which has not previously been inspected in compliance with this article, is inspected. Inspection shall conform to the standards developed pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-1164/" target="_blank" rel="noreferrer">2.2-1164</a>.</p><p>B. The superintendent of schools for each public school division shall certify to the Superintendent of Public Instruction and the Secretary that the public schools in the division have been inspected in compliance with this article.</p><p>C. Inspections completed prior to July 1, 1986, shall be deemed in compliance with this article if the Superintendent of Public Instruction and the Secretary determine that they conform substantially to the standards referenced in § <a href="http://law.lis.virginia.gov/vacode/2.2-1164/" target="_blank" rel="noreferrer">2.2-1164</a>, or to the inspection procedures contained in 40 CFR 763.</p><p>1985, c. 534, § 2.1-526.15; 1986, cc. 288, 560; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',5),a=[c];function r(s,l,p,d,h,f){return i(),t("div",null,a)}const b=e(o,[["render",r]]);export{m as __pageData,b as default};
