import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-1175 Responsibilities of Director.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1175.md","filePath":"vacode/2.2-1175.md"}'),a={name:"vacode/2.2-1175.md"},o=r('<h1 id="§-2-2-1175-responsibilities-of-director" tabindex="-1">§ 2.2-1175 Responsibilities of Director. <a class="header-anchor" href="#§-2-2-1175-responsibilities-of-director" aria-label="Permalink to &quot;§ 2.2-1175 Responsibilities of Director.&quot;">​</a></h1><p>The Director shall establish an appropriate administrative unit within the Department to manage the centralized fleet. The Director&#39;s responsibilities for the centralized fleet shall include, but not be limited to, the following:</p><ol><li><p>Administering the assignment of vehicles to officers and employees of the Commonwealth;</p></li><li><p>Managing a pool of vehicles for short-term use;</p></li><li><p>Purchasing vehicles necessary to the operation of the centralized fleet;</p></li><li><p>Repairing and maintaining vehicles;</p></li><li><p>Monitoring the use of vehicles and enforcing guidance documents regarding their proper use; and</p></li><li><p>Maintaining records related to the operation and maintenance of vehicles, and the administration of the centralized fleet.</p></li></ol><p>1989, c. 479, § 33.1-402; 2001, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0815" target="_blank" rel="noreferrer">815</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0842" target="_blank" rel="noreferrer">842</a>, § 2.1-548.03; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0485" target="_blank" rel="noreferrer">485</a>.</p>',4),n=[o];function l(s,c,p,h,d,f){return i(),t("div",null,n)}const m=e(a,[["render",l]]);export{g as __pageData,m as default};
