import{_ as e,o as r,c as i,O as t}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-1202.1 Additional powers and duties of Director; employment dispute resolution.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1202.1.md","filePath":"vacode/2.2-1202.1.md"}'),a={name:"vacode/2.2-1202.1.md"},n=t('<h1 id="§-2-2-1202-1-additional-powers-and-duties-of-director-employment-dispute-resolution" tabindex="-1">§ 2.2-1202.1 Additional powers and duties of Director; employment dispute resolution. <a class="header-anchor" href="#§-2-2-1202-1-additional-powers-and-duties-of-director-employment-dispute-resolution" aria-label="Permalink to &quot;§ 2.2-1202.1 Additional powers and duties of Director; employment dispute resolution.&quot;">​</a></h1><p>The Director shall:</p><ol><li><p>Establish a comprehensive program of employee relations management that includes alternative processes for resolving employment disputes;</p></li><li><p>Establish the grievance procedure and a statewide mediation program;</p></li><li><p>Adopt rules and set hearing officer fees for grievance hearings;</p></li><li><p>For employees who are covered by the grievance procedure, (i) provide forms necessary for the proper use of the grievance procedure; (ii) direct full compliance with the grievance procedure process; and (iii) investigate allegations of retaliation as the result of use of or participation in the grievance procedure or of reporting, in good faith, an allegation of fraud, waste, or abuse to the Fraud, Waste and Abuse Hotline and advise the agency head of the findings;</p></li><li><p>Render final decisions, containing the reasons for such decision, on all matters related to access to the grievance procedure, procedural compliance with the grievance procedure, and qualification for hearing;</p></li><li><p>Establish a process to select, on a rotating basis, hearing officers for grievance hearings from (i) the list maintained by the Executive Secretary of the Supreme Court or (ii) attorneys hired as classified employees by the Department through a competitive selection process; train and assign such hearing officers to conduct grievance hearings; evaluate the quality of their services to determine eligibility for continued selection; and, if deemed ineligible for continued selection, establish policies for removing such hearing officers from consideration for future selection;</p></li><li><p>Publish hearing officer decisions and Department rulings;</p></li><li><p>Establish a training program for human resources personnel on employee relations management and employment rights and responsibilities;</p></li><li><p>Implement a comprehensive training and instructional program for all supervisory personnel that includes the role of the grievance procedure in harmonious employee relations management. The training program shall also include methods for supervisors to instruct nonsupervisory personnel in the use of the grievance procedure. Use of the grievance procedure to resolve disputes shall be encouraged. In-house resources shall be developed to allow the Department and its personnel to conduct onsite training of this nature for units and agencies of state government throughout Virginia. The Department shall assist agencies in establishing performance criteria for such supervisory personnel;</p></li><li><p>Provide information upon the request of any employee concerning personnel policies, regulations, and law applicable to the grievance procedure and counsel employees in the resolution of conflict in the workplace;</p></li><li><p>Establish and maintain a toll-free telephone number to facilitate access by employees to the services of the Department;</p></li><li><p>Collect information and statistical data regarding the use of the grievance procedure and the effectiveness of employee relations management in the various state agencies;</p></li><li><p>Make recommendations to the Governor and the General Assembly to improve the grievance procedure and employee relations management;</p></li><li><p>Conduct such training seminars and educational programs for the members and staff of agencies and public bodies and other interested persons on the use of dispute resolution proceedings as the Director determines appropriate;</p></li><li><p>Exercise such other powers and perform such other duties as may be requested by the Governor; and</p></li><li><p>Perform all acts and employ such personnel as may be required, necessary, or convenient to carry out the provisions of this section.</p></li></ol><p>1995, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0016" target="_blank" rel="noreferrer">16</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0770" target="_blank" rel="noreferrer">770</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0818" target="_blank" rel="noreferrer">818</a>, § 2.1-116.03; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0164" target="_blank" rel="noreferrer">164</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0263" target="_blank" rel="noreferrer">263</a>; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0066" target="_blank" rel="noreferrer">66</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0947" target="_blank" rel="noreferrer">947</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP1006" target="_blank" rel="noreferrer">1006</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>, § 2.2-1001; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0056" target="_blank" rel="noreferrer">56</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0349" target="_blank" rel="noreferrer">349</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>; 2013, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0572" target="_blank" rel="noreferrer">572</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0690" target="_blank" rel="noreferrer">690</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0717" target="_blank" rel="noreferrer">717</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0723" target="_blank" rel="noreferrer">723</a>.</p>',4),o=[n];function l(s,p,c,g,h,f){return r(),i("div",null,o)}const m=e(a,[["render",l]]);export{u as __pageData,m as default};
