import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-1213 Alternative application for employment for persons with a disability; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1213.md","filePath":"vacode/2.2-1213.md"}'),o={name:"vacode/2.2-1213.md"},r=i('<h1 id="§-2-2-1213-alternative-application-for-employment-for-persons-with-a-disability-report" tabindex="-1">§ 2.2-1213 Alternative application for employment for persons with a disability; report. <a class="header-anchor" href="#§-2-2-1213-alternative-application-for-employment-for-persons-with-a-disability-report" aria-label="Permalink to &quot;§ 2.2-1213 Alternative application for employment for persons with a disability; report.&quot;">​</a></h1><p>A. The Department shall develop an alternative application process for the employment of persons with a disability as defined in § <a href="/vacode/51.5-40.1/">51.5-40.1</a>. Such alternative application process shall be noncompetitive in nature and may be used for the employment of a person with a disability on a permanent or temporary basis. The Department shall include an option for state agencies to convert a position filled through the noncompetitive process created by this subsection into a position that is filled through a competitive process.</p><p>B. The Department shall include in the annual report required under subsection H of § <a href="/vacode/2.2-203.2:3/">2.2-203.2:3</a> information on the status of the alternative application process for persons with a disability, including the total number of persons with a disability who (i) sought state employment through the alternative state application process, (ii) are currently employed through the alternative state application process, and (iii) had their employment in a position filled through the noncompetitive process created by subsection A converted into a position that is filled through a competitive process.</p><p>2021, Sp. Sess. I, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0218" target="_blank" rel="noreferrer">218</a>.</p>',4),n=[r];function s(p,l,c,h,d,m){return t(),a("div",null,n)}const _=e(o,[["render",s]]);export{u as __pageData,_ as default};
