import{_ as o,o as r,c as a,z as e,a as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-125 Governor authorized to accept certain property from Confederate Memorial Literary Society.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-125.md","filePath":"vacode/2.2-125.md"}'),n={name:"vacode/2.2-125.md"},i=e("h1",{id:"§-2-2-125-governor-authorized-to-accept-certain-property-from-confederate-memorial-literary-society",tabindex:"-1"},[t("§ 2.2-125 Governor authorized to accept certain property from Confederate Memorial Literary Society. "),e("a",{class:"header-anchor",href:"#§-2-2-125-governor-authorized-to-accept-certain-property-from-confederate-memorial-literary-society","aria-label":'Permalink to "§ 2.2-125 Governor authorized to accept certain property from Confederate Memorial Literary Society."'},"​")],-1),s=e("p",null,"The Governor may accept, in the name of the Commonwealth, the property known as The White House of the Confederacy, any building that may be erected by the Confederate Memorial Literary Society, and the property known as The Lee House, located at 707 East Franklin Street in the City of Richmond, together with any moneys or other assets, including items being housed and displayed in such buildings or any of them, belonging to the Confederate Memorial Literary Society. The buildings and their contents thereof shall thereafter be preserved and maintained for historic purposes by the Commonwealth. Upon the transfer of title to the property to the Commonwealth, the Governor shall appoint a board of trustees consisting of thirteen persons appointed from the Commonwealth at large, which shall thereafter be charged with the preservation and maintenance of the properties and the administration of any funds that may be received or donated by the Confederate Memorial Literary Society or from any other source. The members of the board first appointed shall be appointed as follows: four for terms of four years, four for terms of three years, four for terms of two years, and one for a term of one year. Subsequent appointments shall be for terms of four years except appointments to fill vacancies, which shall be for the unexpired terms.",-1),h=e("p",null,"The board shall appoint a treasurer, who shall have custody of its funds and shall be bonded in such amount as the board may determine. Expenditures from such funds shall be made by the treasurer as the board directs, for any purpose, in the discretion of the board, consonant with the purpose for which the same are donated.",-1),d=e("p",null,"The board may fix, charge and collect admission fees to the buildings under its custody and control, and expend moneys so received in the upkeep, maintenance and operation of such buildings as historic shrines.",-1),c=e("p",null,[t("1966, c. 412, §§ 9-84.1, 9-84.2, 9-84.3, 9-84.4; 2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844",target:"_blank",rel:"noreferrer"},"844"),t(".")],-1),l=[i,s,h,d,c];function f(p,m,u,y,b,_){return r(),a("div",null,l)}const w=o(n,[["render",f]]);export{v as __pageData,w as default};
