import{_ as e,o as a,c as r,O as n}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-1503.1 Filing of six-year financial outline by Governor.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1503.1.md","filePath":"vacode/2.2-1503.1.md"}'),i={name:"vacode/2.2-1503.1.md"},o=n('<h1 id="§-2-2-1503-1-filing-of-six-year-financial-outline-by-governor" tabindex="-1">§ 2.2-1503.1 Filing of six-year financial outline by Governor. <a class="header-anchor" href="#§-2-2-1503-1-filing-of-six-year-financial-outline-by-governor" aria-label="Permalink to &quot;§ 2.2-1503.1 Filing of six-year financial outline by Governor.&quot;">​</a></h1><p>On or before the first day of each regular session of the General Assembly held in an even-numbered year, the Governor shall prepare and submit to the members of the General Assembly a financial plan for a prospective period of six years. The plan shall consist of (i) the Governor&#39;s biennial budget submitted pursuant to subsection A of § <a href="http://law.lis.virginia.gov/vacode/2.2-1508/" target="_blank" rel="noreferrer">2.2-1508</a>, (ii) estimates of anticipated general fund and nongeneral fund revenue prepared for an additional period of four years pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-1503/" target="_blank" rel="noreferrer">2.2-1503</a>, and (iii) estimates of the general and nongeneral fund appropriations required for each major program for an additional period of four years. In preparing such financial plan, the Governor may utilize the estimate prepared by each agency pursuant to § <a href="http://law.lis.virginia.gov/vacode/2.2-1504/" target="_blank" rel="noreferrer">2.2-1504</a>, or such other information as he may deem necessary.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0480" target="_blank" rel="noreferrer">480</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0486" target="_blank" rel="noreferrer">486</a>.</p>',3),t=[o];function l(s,f,c,d,p,h){return a(),r("div",null,t)}const _=e(i,[["render",l]]);export{u as __pageData,_ as default};
