import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const b=JSON.parse('{"title":"§ 2.2-1510 Publication of budget highlights; public hearings.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1510.md","filePath":"vacode/2.2-1510.md"}'),o={name:"vacode/2.2-1510.md"},n=i('<h1 id="§-2-2-1510-publication-of-budget-highlights-public-hearings" tabindex="-1">§ 2.2-1510 Publication of budget highlights; public hearings. <a class="header-anchor" href="#§-2-2-1510-publication-of-budget-highlights-public-hearings" aria-label="Permalink to &quot;§ 2.2-1510 Publication of budget highlights; public hearings.&quot;">​</a></h1><p>A. The Governor shall ensure that a summary of the highlights of each budget submitted pursuant to § <a href="/vacode/2.2-1508/">2.2-1508</a> and set of amendments submitted pursuant to subsection E of § <a href="/vacode/2.2-1509/">2.2-1509</a> are sent to a newspaper of general circulation in the following geographical areas of the Commonwealth: Northern Virginia, Hampton Roads, Richmond/Petersburg, Central Virginia, Shenandoah Valley, Roanoke Valley, Southside, and Southwest Virginia prior to the convening of each session of the General Assembly.</p><p>B. The House Committee on Appropriations and the Senate Committee on Finance and Appropriations shall hold at least four regional public hearings on the budget bill submitted by the Governor. The four public hearings shall be held prior to the convening of such session of the General Assembly, at hearing sites and times as selected by the chairmen of the two committees.</p><p>1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0467" target="_blank" rel="noreferrer">467</a>, § 2.1-399.2; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),r=[n];function s(h,l,c,g,u,d){return t(),a("div",null,r)}const f=e(o,[["render",s]]);export{b as __pageData,f as default};
