import{_ as e,o as t,c as r,O as a}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-1514 (Contingent effective date) Commitment of general fund for nonrecurring expenditures.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1514.md","filePath":"vacode/2.2-1514.md"}'),n={name:"vacode/2.2-1514.md"},i=a('<h1 id="§-2-2-1514-contingent-effective-date-commitment-of-general-fund-for-nonrecurring-expenditures" tabindex="-1">§ 2.2-1514 (Contingent effective date) Commitment of general fund for nonrecurring expenditures. <a class="header-anchor" href="#§-2-2-1514-contingent-effective-date-commitment-of-general-fund-for-nonrecurring-expenditures" aria-label="Permalink to &quot;§ 2.2-1514 (Contingent effective date) Commitment of general fund for nonrecurring expenditures.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;The Budget Bill&quot; means &quot;The Budget Bill&quot; submitted pursuant to § <a href="/vacode/2.2-1509/">2.2-1509</a>, including any amendments to a general appropriation act pursuant to such section.</p><p>&quot;Nonrecurring expenditures&quot; means the acquisition or construction of capital outlay projects as defined in § <a href="/vacode/2.2-1518/">2.2-1518</a>, the acquisition or construction of capital improvements, the acquisition of land, the acquisition of equipment, or other expenditures of a one-time nature as specified in the general appropriation act.</p><p>B. At the end of each fiscal year, the Comptroller shall commit within his annual report pursuant to § <a href="/vacode/2.2-813/">2.2-813</a> as follows: 67 percent of the remaining amount of the general fund balance that is not otherwise restricted, committed, or assigned for other usage within the general fund shall be committed by the Comptroller for deposit into the Commonwealth Transportation Fund established pursuant to § <a href="/vacode/33.2-1524/">33.2-1524</a> or a subfund thereof, and the remaining amount shall be committed for nonrecurring expenditures. No such commitment shall be made unless the full amounts required for other restrictions, commitments, or assignments including but not limited to (i) the Revenue Stabilization Fund deposit pursuant to § <a href="/vacode/2.2-1829/">2.2-1829</a>, (ii) the Virginia Water Quality Improvement Fund deposit pursuant to § <a href="/vacode/10.1-2128/">10.1-2128</a>, but excluding any deposits provided under the Virginia Natural Resources Commitment Fund established under § <a href="/vacode/10.1-2128.1/">10.1-2128.1</a>, (iii) capital outlay reappropriations pursuant to the general appropriation act, (iv) (a) operating expense reappropriations pursuant to the general appropriation act, and (b) reappropriations of unexpended appropriations to certain public institutions of higher education pursuant to § <a href="/vacode/23.1-1002/">23.1-1002</a>, (v) pro rata rebate payments to certain public institutions of higher education pursuant to § <a href="/vacode/23.1-1002/">23.1-1002</a>, (vi) the unappropriated balance anticipated in the general appropriation act for the end of such fiscal year, (vii) interest payments on deposits of certain public institutions of higher education pursuant to § <a href="/vacode/23.1-1002/">23.1-1002</a>, and (viii) the Revenue Reserve Fund deposit pursuant to § <a href="/vacode/2.2-1831.2/">2.2-1831.2</a> are set aside. The Comptroller shall set aside amounts required for clauses (iv) (b), (v), and (vii) beginning with the initial fiscal year as determined under § <a href="/vacode/23.1-1002/">23.1-1002</a> and for all fiscal years thereafter.</p><p>C. The Governor shall include in &quot;The Budget Bill&quot; pursuant to § <a href="/vacode/2.2-1509/">2.2-1509</a> recommended appropriations from the general fund or recommended amendments to general fund appropriations in the general appropriation act in effect at that time an amount for deposit into the Commonwealth Transportation Fund or a subfund thereof, and an amount for nonrecurring expenditures equal to the amount committed by the Comptroller for such purpose pursuant to the provisions of subsection B. Such deposit to the Commonwealth Transportation Fund or a subfund thereof shall not preclude the appropriation of additional amounts from the general fund for transportation purposes.</p><p>2002, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0839" target="_blank" rel="noreferrer">839</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?021+ful+CHAP0888" target="_blank" rel="noreferrer">888</a>; 2005, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0933" target="_blank" rel="noreferrer">933</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0945" target="_blank" rel="noreferrer">945</a>; 2008, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0643" target="_blank" rel="noreferrer">643</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0701" target="_blank" rel="noreferrer">701</a>; 2008, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0001" target="_blank" rel="noreferrer">1</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?081+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0684" target="_blank" rel="noreferrer">684</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0729" target="_blank" rel="noreferrer">729</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0733" target="_blank" rel="noreferrer">733</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0827" target="_blank" rel="noreferrer">827</a>; 2020, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1230" target="_blank" rel="noreferrer">1230</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP1275" target="_blank" rel="noreferrer">1275</a>.</p>',7),o=[i];function s(l,u,p,c,f,d){return t(),r("div",null,o)}const m=e(n,[["render",s]]);export{g as __pageData,m as default};
