import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-1800 Department of the Treasury; State Treasurer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1800.md","filePath":"vacode/2.2-1800.md"}'),o={name:"vacode/2.2-1800.md"},s=a('<h1 id="§-2-2-1800-department-of-the-treasury-state-treasurer" tabindex="-1">§ 2.2-1800 Department of the Treasury; State Treasurer. <a class="header-anchor" href="#§-2-2-1800-department-of-the-treasury-state-treasurer" aria-label="Permalink to &quot;§ 2.2-1800 Department of the Treasury; State Treasurer.&quot;">​</a></h1><p>A. There is created a Department of the Treasury (&quot;the Department&quot;), which shall be under the direct control and supervision of the State Treasurer. The State Treasurer shall be appointed by the Governor to serve at his pleasure or until a successor is appointed and qualified.</p><p>B. The State Treasurer shall, under the direction and control of the Governor, exercise the powers and perform the duties conferred or imposed by law upon him and shall perform such other duties as may be required by the Governor.</p><p>Code 1950, § 2-149; 1966, c. 677, § 2.1-177; 1971, Ex. Sess., c. 125; 1976, c. 728; 1984, c. 720; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0025" target="_blank" rel="noreferrer">25</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0275" target="_blank" rel="noreferrer">275</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),n=[s];function i(c,h,l,p,d,u){return r(),t("div",null,n)}const m=e(o,[["render",i]]);export{f as __pageData,m as default};
