import{_ as t,o as a,c as r,z as e,a as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-1831.2 Creation of Revenue Reserve Fund.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1831.2.md","filePath":"vacode/2.2-1831.2.md"}'),s={name:"vacode/2.2-1831.2.md"},o=e("h1",{id:"§-2-2-1831-2-creation-of-revenue-reserve-fund",tabindex:"-1"},[n("§ 2.2-1831.2 Creation of Revenue Reserve Fund. "),e("a",{class:"header-anchor",href:"#§-2-2-1831-2-creation-of-revenue-reserve-fund","aria-label":'Permalink to "§ 2.2-1831.2 Creation of Revenue Reserve Fund."'},"​")],-1),i=e("p",null,'There is hereby created in the state treasury a special nonreverting fund to be known as the Revenue Reserve Fund, referred to in this article as "the Fund." The Fund shall be established on the books of the Comptroller. All funds appropriated for such purpose and any gifts, donations, grants, bequests, and other funds received on its behalf shall be paid into the state treasury and credited to the Fund. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund. Moneys in the Fund shall be used to offset, in whole or in part, certain anticipated shortfalls in revenues when appropriations based on previous forecasts exceed expected revenues in subsequent forecasts as provided in § 2.2-1831.4.',-1),d=e("p",null,[n("2018, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0827",target:"_blank",rel:"noreferrer"},"827"),n(".")],-1),l=[o,i,d];function h(c,u,f,p,v,_){return a(),r("div",null,l)}const g=t(s,[["render",h]]);export{m as __pageData,g as default};
