import{_ as e,o as r,c as t,O as a}from"./chunks/framework.9482e208.js";const u=JSON.parse('{"title":"§ 2.2-1841 Blanket surety bond plan for moneys under control of court.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-1841.md","filePath":"vacode/2.2-1841.md"}'),o={name:"vacode/2.2-1841.md"},n=a('<h1 id="§-2-2-1841-blanket-surety-bond-plan-for-moneys-under-control-of-court" tabindex="-1">§ 2.2-1841 Blanket surety bond plan for moneys under control of court. <a class="header-anchor" href="#§-2-2-1841-blanket-surety-bond-plan-for-moneys-under-control-of-court" aria-label="Permalink to &quot;§ 2.2-1841 Blanket surety bond plan for moneys under control of court.&quot;">​</a></h1><p>The Division shall establish a program of blanket surety bonding to provide surety for the faithful discharge of duty with respect to moneys held pursuant to §§ <a href="http://law.lis.virginia.gov/vacode/8.01-582/" target="_blank" rel="noreferrer">8.01-582</a> and <a href="http://law.lis.virginia.gov/vacode/8.01-600/" target="_blank" rel="noreferrer">8.01-600</a> by all general receivers and clerks. General receivers and clerks shall participate in the program. The Division&#39;s cost of obtaining and administering the blanket surety bond shall be paid from those moneys covered by the bond.</p><p>1988, c. 841, § 2.1-526.9:1; 2000, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0618" target="_blank" rel="noreferrer">618</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0632" target="_blank" rel="noreferrer">632</a>, § 2.1-191.14; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',3),l=[n];function i(s,c,d,f,h,p){return r(),t("div",null,l)}const g=e(o,[["render",i]]);export{u as __pageData,g as default};
