import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse(`{"title":"§ 2.2-200 Appointment of Governor's Secretaries; general powers; severance.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-200.md","filePath":"vacode/2.2-200.md"}`),i={name:"vacode/2.2-200.md"},n=t('<h1 id="§-2-2-200-appointment-of-governor-s-secretaries-general-powers-severance" tabindex="-1">§ 2.2-200 Appointment of Governor&#39;s Secretaries; general powers; severance. <a class="header-anchor" href="#§-2-2-200-appointment-of-governor-s-secretaries-general-powers-severance" aria-label="Permalink to &quot;§ 2.2-200 Appointment of Governor&#39;s Secretaries; general powers; severance.&quot;">​</a></h1><p>A. The Governor&#39;s Secretaries shall be appointed by the Governor, subject to confirmation by the General Assembly if in session when the appointment is made, and if not in session, then at its next succeeding session. Each Secretary shall hold office at the pleasure of the Governor for a term coincident with that of the Governor making the appointment or until a successor is appointed and qualified. Before entering upon the discharge of duties, each Secretary shall take an oath to faithfully execute the duties of the office.</p><p>B. Each Secretary shall be subject to direction and supervision by the Governor. Except as provided in Article 4 (§ <a href="/vacode/2.2-208/">2.2-208</a> et seq.), the agencies assigned to each Secretary shall:</p><ol><li><p>Exercise their respective powers and duties in accordance with the general policy established by the Governor or by the Secretary acting on behalf of the Governor;</p></li><li><p>Provide such assistance to the Governor or the Secretary as may be required; and</p></li><li><p>Forward all reports to the Governor through the Secretary.</p></li></ol><p>C. Unless the Governor expressly reserves such power to himself and except as provided in Article 4 (§ <a href="/vacode/2.2-208/">2.2-208</a> et seq.), each Secretary may:</p><ol><li><p>Resolve administrative, jurisdictional, operational, program, or policy conflicts between agencies or officials assigned;</p></li><li><p>Direct the formulation of a comprehensive program budget for the functional area identified in § <a href="/vacode/2.2-1508/">2.2-1508</a> encompassing the services of agencies assigned for consideration by the Governor;</p></li><li><p>Hold agency heads accountable for their administrative, fiscal and program actions in the conduct of the respective powers and duties of the agencies;</p></li><li><p>Direct the development of goals, objectives, policies and plans that are necessary to the effective and efficient operation of government;</p></li><li><p>Sign documents on behalf of the Governor that originate with agencies assigned to the Secretary; and</p></li><li><p>Employ such personnel and to contract for such consulting services as may be required to perform the powers and duties conferred upon the Secretary by law or executive order.</p></li></ol><p>D. Severance benefits provided to any departing Secretary shall be publicly announced by the Governor prior to such departure.</p><p>E. As used in this chapter, &quot;Governor&#39;s Secretaries&quot; means the Secretary of Administration, the Secretary of Agriculture and Forestry, the Secretary of Commerce and Trade, the Secretary of Education, the Secretary of Finance, the Secretary of Health and Human Resources, the Secretary of Labor, the Secretary of Natural and Historic Resources, the Secretary of Public Safety and Homeland Security, the Secretary of Transportation, and the Secretary of Veterans and Defense Affairs.</p><p>1972, c. 641, § 2.1-51.7; 1975, c. 390; 1976, cc. 729, 730, 732, 733, 734, 743, §§ 2.1-51.8:1, 2.1-51.13, 2.1-51.14, 2.1-51.16, 2.1-51.17, 2.1-51.26; 1984, c. 720, § 2.1-51.33; 1986, c. 492, § 2.1-51.39; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0646" target="_blank" rel="noreferrer">646</a>; 1990, cc. 1, 317, §§ 2.1-51.41, 2.1-51.42; 1993, c. 699; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0500" target="_blank" rel="noreferrer">500</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0617" target="_blank" rel="noreferrer">617</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0793" target="_blank" rel="noreferrer">793</a>; 1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0412" target="_blank" rel="noreferrer">412</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0421" target="_blank" rel="noreferrer">421</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0433" target="_blank" rel="noreferrer">433</a>, §§ 2.1-51.44, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>.1-51.45; 2000, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?001+ful+CHAP0937" target="_blank" rel="noreferrer">937</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2004, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0940" target="_blank" rel="noreferrer">940</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0963" target="_blank" rel="noreferrer">963</a>; 2006, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?061+ful+CHAP0254" target="_blank" rel="noreferrer">254</a>; 2011, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0780" target="_blank" rel="noreferrer">780</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0858" target="_blank" rel="noreferrer">858</a>; 2014, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0115" target="_blank" rel="noreferrer">115</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0490" target="_blank" rel="noreferrer">490</a>; 2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0738" target="_blank" rel="noreferrer">738</a>; 2021, Sp. Sess. I, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0401" target="_blank" rel="noreferrer">401</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?212+ful+CHAP0453" target="_blank" rel="noreferrer">453</a>.</p>',9),o=[n];function l(c,s,p,h,f,g){return r(),a("div",null,o)}const u=e(i,[["render",l]]);export{v as __pageData,u as default};
