import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const _=JSON.parse('{"title":"§ 2.2-2000 Department of Veterans Services created; appointment of Commissioner.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2000.md","filePath":"vacode/2.2-2000.md"}'),o={name:"vacode/2.2-2000.md"},n=t('<h1 id="§-2-2-2000-department-of-veterans-services-created-appointment-of-commissioner" tabindex="-1">§ 2.2-2000 Department of Veterans Services created; appointment of Commissioner. <a class="header-anchor" href="#§-2-2-2000-department-of-veterans-services-created-appointment-of-commissioner" aria-label="Permalink to &quot;§ 2.2-2000 Department of Veterans Services created; appointment of Commissioner.&quot;">​</a></h1><p>A. There shall be a Department of Veterans Services, which shall be headed by a Commissioner appointed by the Governor subject to confirmation by the General Assembly. The Commissioner shall be a veteran who has received an honorable discharge from the Armed Forces of the United States. He shall report to the Secretary of Veterans and Defense Affairs on behalf of the Governor and shall hold his office at the pleasure of the Governor for a term of five years.</p><p>B. The Commissioner shall, under the direction and control of the Governor, exercise powers and perform duties conferred or imposed upon him by law and perform such other duties as may be required by the Governor and the Secretary of Veterans and Defense Affairs.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0657" target="_blank" rel="noreferrer">657</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0670" target="_blank" rel="noreferrer">670</a>; 2004, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?041+ful+CHAP0697" target="_blank" rel="noreferrer">697</a>; 2018, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0648" target="_blank" rel="noreferrer">648</a>.</p>',4),i=[n];fun