import{_ as e,o as t,c as n,O as i}from"./chunks/framework.9482e208.js";const l=JSON.parse(`{"title":"§ 2.2-2001.5 Assignment of right to receive veterans' benefits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2001.5.md","filePath":"vacode/2.2-2001.5.md"}`),r={name:"vacode/2.2-2001.5.md"},a=i('<h1 id="§-2-2-2001-5-assignment-of-right-to-receive-veterans-benefits" tabindex="-1">§ 2.2-2001.5 Assignment of right to receive veterans&#39; benefits. <a class="header-anchor" href="#§-2-2-2001-5-assignment-of-right-to-receive-veterans-benefits" aria-label="Permalink to &quot;§ 2.2-2001.5 Assignment of right to receive veterans&#39; benefits.&quot;">​</a></h1><p>A. As used in this section:</p><p>&quot;Assignment of right to receive veterans&#39; benefits&quot; means any financial transaction in which a person provides a cash payment to a veteran in consideration for the veteran&#39;s assignment of his right to receive future pension or retirement benefits, without regard to whether the transaction is characterized or structured as a loan, assignment, loan secured by assignment, pledge, or other arrangement.</p><p>&quot;Pension or retirement benefits&quot; means any periodic benefit payable to a veteran by an agency of the federal government on account of the veteran&#39;s service in the Armed Forces of the United States, including any military retirement, pension, or disability benefit payments.</p><p>B. No person shall advertise, arrange, offer, or enter into any assignment of right to receive veterans&#39; benefits if such assignment of right to receive veterans&#39; benefits is prohibited or void under the provisions of 37 U.S.C. § 701 or 38 U.S.C. § 5301(a).</p><p>C. A violation of this section constitutes a prohibited practice under the provisions of § <a href="/vacode/59.1-200/">59.1-200</a> and shall be subject to any and all of the enforcement provisions of the Virginia Consumer Protection Act (§ <a href="/vacode/59.1-196/">59.1-196</a> et seq.).</p><p>2020, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?201+ful+CHAP0438" target="_blank" rel="noreferrer">438</a>.</p>',7),o=[a];function s(c,f,h,d,p,v){return t(),n("div",null,o)}const m=e(r,[["render",s]]);export{l as __pageData,m as default};
