import{_ as e,o as i,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-2004.2 Suicide Prevention Coordinator; position created; duties; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2004.2.md","filePath":"vacode/2.2-2004.2.md"}'),t={name:"vacode/2.2-2004.2.md"},o=r('<h1 id="§-2-2-2004-2-suicide-prevention-coordinator-position-created-duties-report" tabindex="-1">§ 2.2-2004.2 Suicide Prevention Coordinator; position created; duties; report. <a class="header-anchor" href="#§-2-2-2004-2-suicide-prevention-coordinator-position-created-duties-report" aria-label="Permalink to &quot;§ 2.2-2004.2 Suicide Prevention Coordinator; position created; duties; report.&quot;">​</a></h1><p>A. There is created in the Department of Veterans Services the position of Suicide Prevention Coordinator to support and closely coordinate effective mental health care services for military service members and veterans and their families.</p><p>B. The Suicide Prevention Coordinator shall:</p><ol><li><p>Gather data on mental health challenges commonly experienced by military service members and veterans and their families that may lead to suicide;</p></li><li><p>In coordination with federal, state, and local partners, gather, review, analyze, and disseminate timely federal, state, and local data on the quantity, common causes, and methods of suicide utilized among military service members, veterans, and their family members;</p></li><li><p>Collaborate with federal, state, and local partners to increase mental health, substance abuse, and suicide risk screenings and refer military service members and veterans and their family members to the appropriate behavioral health and medical professionals or services;</p></li><li><p>Identify and coordinate new behavioral health and suicide prevention opportunities and funding for those opportunities and, from such funds as may be appropriated or are otherwise available for this purpose, administer a grant program to assist local partners in implementing and coordinating suicide prevention efforts in local communities;</p></li><li><p>Provide suicide prevention resources, training, and support to federal, state, and local agencies; and</p></li><li><p>Perform other duties as may be required to effectively provide suicide prevention resources, training, and support to military service members, veterans, and their families.</p></li></ol><p>C. The Commissioner shall include a summary of the work of the Suicide Prevention Coordinator in the annual report to the Secretary of Veterans and Defense Affairs, the Governor, and the General Assembly as required pursuant to subdivision 9 of § <a href="/vacode/2.2-2004/">2.2-2004</a>.</p><p>2022, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?221+ful+CHAP0322" target="_blank" rel="noreferrer">322</a>.</p>',6),n=[o];function s(d,l,c,p,m,h){return i(),a("div",null,n)}const v=e(t,[["render",s]]);export{f as __pageData,v as default};
