import{_ as e,o,c as t,O as i}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-2017 Powers and duties of the Division.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2017.md","filePath":"vacode/2.2-2017.md"}'),n={name:"vacode/2.2-2017.md"},a=i('<h1 id="§-2-2-2017-powers-and-duties-of-the-division" tabindex="-1">§ 2.2-2017 Powers and duties of the Division. <a class="header-anchor" href="#§-2-2-2017-powers-and-duties-of-the-division" aria-label="Permalink to &quot;§ 2.2-2017 Powers and duties of the Division.&quot;">​</a></h1><p>The Division shall have the power and duty to:</p><ol><li><p>Implement the approval process for information technology projects developed in accordance with the Commonwealth Project Management Standard;</p></li><li><p>Assist the CIO in the development and implementation of project management policies, standards, and guidelines to be used for information technology projects in accordance with this article;</p></li><li><p>Provide ongoing assistance and support to executive branch agencies in the development of information technology projects;</p></li><li><p>Establish a program providing cost-effective training to executive branch agency project managers;</p></li><li><p>Review information management and information technology plans submitted by executive branch agencies and recommend to the CIO the approval of such plans and any amendments thereto;</p></li><li><p>Monitor the implementation of information management and information technology plans and periodically report its findings to the CIO;</p></li><li><p>Review and recommend to the CIO information technology projects based on the policies, standards, and guidelines developed pursuant to § <a href="/vacode/2.2-2016.1/">2.2-2016.1</a>;</p></li><li><p>Provide oversight for executive branch agency information technology projects; and</p></li><li><p>Report on a quarterly basis to the CIO, the Secretary, the Governor, the Information Technology Advisory Council, the Joint Legislative Audit and Review Commission, the Auditor of Public Accounts, the House Committee on Appropriations, the Senate Committee on Finance and Appropriations, and the Joint Commission on Technology and Science the status and performance of each major information technology project and related procurement conducted by any executive branch agency.</p></li></ol><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0739" target="_blank" rel="noreferrer">739</a>; 2015, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?151+ful+CHAP0768" target="_blank" rel="noreferrer">768</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0296" target="_blank" rel="noreferrer">296</a>.</p>',4),r=[a];function s(c,l,p,d,h,m){return o(),t("div",null,r)}const v=e(n,[["render",s]]);export{f as __pageData,v as default};
