import{_ as e,o as r,c as o,O as t}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-2020 Procurement approval for information technology projects.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2020.md","filePath":"vacode/2.2-2020.md"}'),a={name:"vacode/2.2-2020.md"},n=t('<h1 id="§-2-2-2020-procurement-approval-for-information-technology-projects" tabindex="-1">§ 2.2-2020 Procurement approval for information technology projects. <a class="header-anchor" href="#§-2-2-2020-procurement-approval-for-information-technology-projects" aria-label="Permalink to &quot;§ 2.2-2020 Procurement approval for information technology projects.&quot;">​</a></h1><p>An executive branch agency shall submit a copy of any Invitation for Bid (IFB) or Request for Proposal (RFP) for a procurement related to an information technology project to the Division. The Division shall review the IFB or RFP and recommend its approval or rejection to the CIO. The agency shall submit a copy of any proposed contract or final contract to the Division. The Division shall review the proposed contract or final contract and recommend its approval or rejection to the CIO. A project shall be granted project initiation approval as provided by the Commonwealth Project Management Standard before the award of any contract.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0981" target="_blank" rel="noreferrer">981</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP1021" target="_blank" rel="noreferrer">1021</a>; 2010, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0136" target="_blank" rel="noreferrer">136</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0145" target="_blank" rel="noreferrer">145</a>; 2011, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?111+ful+CHAP0739" target="_blank" rel="noreferrer">739</a>; 2016, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?161+ful+CHAP0296" target="_blank" rel="noreferrer">296</a>.</p>',3),i=[n];function c(l,p,s,f,h,g){return r(),o("div",null,i)}const d=e(a,[["render",c]]);export{v as __pageData,d as default};
