import{_ as e,o as t,c as a,O as r}from"./chunks/framework.9482e208.js";const f=JSON.parse('{"title":"§ 2.2-203.2:1 Secretary to report state job elimination due to privatization.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-203.2:1.md","filePath":"vacode/2.2-203.2:1.md"}'),i={name:"vacode/2.2-203.2:1.md"},o=r('<h1 id="§-2-2-203-2-1-secretary-to-report-state-job-elimination-due-to-privatization" tabindex="-1">§ 2.2-203.2:1 Secretary to report state job elimination due to privatization. <a class="header-anchor" href="#§-2-2-203-2-1-secretary-to-report-state-job-elimination-due-to-privatization" aria-label="Permalink to &quot;§ 2.2-203.2:1 Secretary to report state job elimination due to privatization.&quot;">​</a></h1><p>On or before November 30 of each year, the Secretary shall report to the Governor and the General Assembly on the number of state jobs eliminated in the immediately preceding fiscal year due to the privatization of commercial activities to a commercial source.</p><p>As used in this section, unless the context requires a different meaning:</p><p>&quot;Commercial activities&quot; means an activity performed by or for state government that is not an inherently governmental activity and that may feasibly be obtained from a commercial source at lower cost than the activity being performed by state employees.</p><p>&quot;Commercial source&quot; means any business or other concern that is eligible for a contract award in accordance with the Virginia Public Procurement Act (§ <a href="http://law.lis.virginia.gov/vacode/2.2-4300/" target="_blank" rel="noreferrer">2.2-4300</a> et seq.).</p><p>2005, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?051+ful+CHAP0476" target="_blank" rel="noreferrer">476</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0803" target="_blank" rel="noreferrer">803</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0835" target="_blank" rel="noreferrer">835</a>.</p>',6),n=[o];function c(s,l,p,m,d,h){return t(),a("div",null,n)}const u=e(i,[["render",c]]);export{f as __pageData,u as default};
