import{_ as t,o as a,c as n,z as e,a as i}from"./chunks/framework.9482e208.js";const v=JSON.parse('{"title":"§ 2.2-213.5 Dissemination of information about specialized training to prevent and minimize mental health crisis.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-213.5.md","filePath":"vacode/2.2-213.5.md"}'),o={name:"vacode/2.2-213.5.md"},s=e("h1",{id:"§-2-2-213-5-dissemination-of-information-about-specialized-training-to-prevent-and-minimize-mental-health-crisis",tabindex:"-1"},[i("§ 2.2-213.5 Dissemination of information about specialized training to prevent and minimize mental health crisis. "),e("a",{class:"header-anchor",href:"#§-2-2-213-5-dissemination-of-information-about-specialized-training-to-prevent-and-minimize-mental-health-crisis","aria-label":'Permalink to "§ 2.2-213.5 Dissemination of information about specialized training to prevent and minimize mental health crisis."'},"​")],-1),r=e("p",null,"The Secretary of Health and Human Resources and the Secretary of Public Safety and Homeland Security shall encourage the dissemination of information about specialized training in evidence-based strategies to prevent and minimize mental health crises in all jurisdictions. This information shall be disseminated to, but not limited to, law-enforcement personnel, other first responders, hospital emergency department personnel, school personnel, and other interested parties, to the extent possible. These strategies shall include (i) crisis intervention team (CIT) training for law-enforcement personnel and other first responders as designated by the community CIT task force and (ii) mental health first aid training for other first responders, hospital emergency department personnel, school personnel, and other interested parties. The Secretary of Health and Human Resources and the Secretary of Public Safety and Homeland Security shall encourage adherence to the models of training and achievement of programmatic goals and standards. The goals for CIT training shall include (i) training participants to recognize the signs and symptoms of behavioral health disorders; (ii) teaching participants the skills necessary to de-escalate crisis situations and how to support individuals in crisis; (iii) educating participants about community-based resources available to individuals in crisis; and (iv) enhancing participants' ability to communicate with health systems about the nature of the crisis to include rules regarding confidentiality and protected health information. The goals for mental health first aid training shall be to teach the public (to include first responders, school personnel, and other interested parties) how to recognize symptoms of mental health problems, how to offer and provide initial help, and how to guide a person toward appropriate treatments and other supportive help.",-1),l=e("p",null,[i("2014, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?141+ful+CHAP0601",target:"_blank",rel:"noreferrer"},"601"),i(".")],-1),d=[s,r,l];function c(h,m,p,f,u,g){return a(),n("div",null,d)}const _=t(o,[["render",c]]);export{v as __pageData,_ as default};
