import{_ as e,o as t,c as r,O as n}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-214.1 Healthy Lives Prescription Fund; nonreverting; purposes; report.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-214.1.md","filePath":"vacode/2.2-214.1.md"}'),a={name:"vacode/2.2-214.1.md"},o=n('<h1 id="§-2-2-214-1-healthy-lives-prescription-fund-nonreverting-purposes-report" tabindex="-1">§ 2.2-214.1 Healthy Lives Prescription Fund; nonreverting; purposes; report. <a class="header-anchor" href="#§-2-2-214-1-healthy-lives-prescription-fund-nonreverting-purposes-report" aria-label="Permalink to &quot;§ 2.2-214.1 Healthy Lives Prescription Fund; nonreverting; purposes; report.&quot;">​</a></h1><p>A. There is hereby created in the Department of the Treasury a special nonreverting fund that shall be known as the Healthy Lives Prescription Fund.</p><p>B. The Fund shall be established on the books of the Comptroller. The Fund shall consist of such moneys appropriated by the General Assembly and any funds available from the federal government, donations, grants, and in-kind contributions made to the Fund for the purposes stated herein. Interest earned on moneys in the Fund shall remain in the Fund and be credited to it. Any moneys remaining in the Fund, including interest thereon, at the end of each fiscal year shall not revert to the general fund but shall remain in the Fund.</p><p>C. Moneys in the Fund shall be available to develop and implement programs that will enhance current prescription drug programs for citizens of the Commonwealth who are without insurance or ability to pay for prescription drugs and to develop innovative programs to make such prescription drugs more available.</p><p>D. The Secretary shall provide an annual report on the status of the Fund and efforts to meet the goals of the Fund.</p><p>2003, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0661" target="_blank" rel="noreferrer">661</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0674" target="_blank" rel="noreferrer">674</a>.</p>',6),i=[o];function s(l,p,h,d,c,u){return t(),r("div",null,i)}const _=e(a,[["render",s]]);export{m as __pageData,_ as default};
