import{_ as e,o as t,c as a,O as i}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2202 Declaration of public purpose; Virginia Commercial Space Flight Authority created.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2202.md","filePath":"vacode/2.2-2202.md"}'),r={name:"vacode/2.2-2202.md"},o=i('<h1 id="§-2-2-2202-declaration-of-public-purpose-virginia-commercial-space-flight-authority-created" tabindex="-1">§ 2.2-2202 Declaration of public purpose; Virginia Commercial Space Flight Authority created. <a class="header-anchor" href="#§-2-2-2202-declaration-of-public-purpose-virginia-commercial-space-flight-authority-created" aria-label="Permalink to &quot;§ 2.2-2202 Declaration of public purpose; Virginia Commercial Space Flight Authority created.&quot;">​</a></h1><p>The General Assembly has determined that there exists in the Commonwealth a need to promote industrial and economic development and scientific and technological research and development through the development and promotion of the commercial and government aerospace industry.</p><p>In order to facilitate and coordinate the advancement of these needs, there is hereby created the Virginia Commercial Space Flight Authority, with the powers and duties set forth in this article, as a public body corporate and as a political subdivision of the Commonwealth. The Authority is constituted as a public instrumentality exercising public functions, and the exercise by the Authority of the powers and duties conferred by this article shall be deemed and held to be the performance of an essential government function of the Commonwealth and a public purpose.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0758" target="_blank" rel="noreferrer">758</a>, § 9-266.3; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2012, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0779" target="_blank" rel="noreferrer">779</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?121+ful+CHAP0817" target="_blank" rel="noreferrer">817</a>; 2017, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?171+ful+CHAP0633" target="_blank" rel="noreferrer">633</a>.</p>',4),n=[o];function c(l,s,h,p,d,u){return t(),a("div",null,n)}const g=e(r,[["render",c]]);export{m as __pageData,g as default};
