import{_ as e,o as i,c as t,O as r}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-2236 Powers and duties of the Chief Executive Officer.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2236.md","filePath":"vacode/2.2-2236.md"}'),a={name:"vacode/2.2-2236.md"},o=r('<h1 id="§-2-2-2236-powers-and-duties-of-the-chief-executive-officer" tabindex="-1">§ 2.2-2236 Powers and duties of the Chief Executive Officer. <a class="header-anchor" href="#§-2-2-2236-powers-and-duties-of-the-chief-executive-officer" aria-label="Permalink to &quot;§ 2.2-2236 Powers and duties of the Chief Executive Officer.&quot;">​</a></h1><p>The Chief Executive Officer shall employ or retain such agents or employees subordinate to the Chief Executive Officer as may be necessary to fulfill the duties of the Authority conferred upon the Chief Executive Officer, subject to the Board&#39;s approval. Employees of the Authority shall be eligible for membership in the Virginia Retirement System and participation in all of the health and related insurance and other benefits, including premium conversion and flexible benefits, available to state employees as provided by law. The Chief Executive Officer shall also exercise such of the powers and duties relating to the direction of the Commonwealth&#39;s economic development efforts conferred upon the Authority as may be delegated to him by the Board, including powers and duties involving the exercise of discretion. The Chief Executive Officer shall also exercise and perform such other powers and duties as may be lawfully delegated to him or as may be conferred or imposed upon him by law.</p><p>1995, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?951+ful+CHAP0638" target="_blank" rel="noreferrer">638</a>, § 2.1-548.32; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0590" target="_blank" rel="noreferrer">590</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0598" target="_blank" rel="noreferrer">598</a>; 1998, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?981+ful+CHAP0359" target="_blank" rel="noreferrer">359</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2010, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?101+ful+CHAP0869" target="_blank" rel="noreferrer">869</a>.</p>',3),n=[o];function l(s,f,c,h,d,p){return i(),t("div",null,n)}const _=e(a,[["render",l]]);export{g as __pageData,_ as default};
