import{_ as o,o as n,c as r,z as e,a as t}from"./chunks/framework.9482e208.js";const y=JSON.parse('{"title":"§ 2.2-2265 Trust agreement securing bonds.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2265.md","filePath":"vacode/2.2-2265.md"}'),a={name:"vacode/2.2-2265.md"},s=e("h1",{id:"§-2-2-2265-trust-agreement-securing-bonds",tabindex:"-1"},[t("§ 2.2-2265 Trust agreement securing bonds. "),e("a",{class:"header-anchor",href:"#§-2-2-2265-trust-agreement-securing-bonds","aria-label":'Permalink to "§ 2.2-2265 Trust agreement securing bonds."'},"​")],-1),i=e("p",null,"In the discretion of the Authority any bonds issued under the provisions of this article may be secured by a trust agreement by and between the Authority and a corporate trustee, which may be any trust company or bank having the powers of a trust company within or without the Commonwealth. The trust agreement or the resolution providing for the issuance of the bonds may pledge or assign the revenues to be received, but shall not convey or mortgage any project or any part thereof. The trust agreement or resolution providing for the issuance of such bonds may contain such provisions for protecting and enforcing the rights and remedies of the bondholders as may be reasonable and proper and not in violation of law, including covenants setting forth the duties of the Authority in relation to the acquisition of property and the acquisition, construction, establishment, improvement, extension, enlargement, maintenance, repair, operation and insurance of the project in connection with which the bonds have been authorized, the rates and fees to be charged, the custody, safeguarding and application of all moneys, and conditions or limitations with respect to the issuance of additional bonds. It shall be lawful for any bank or trust company incorporated under the laws of the Commonwealth which may act as depository of the proceeds of bonds or of revenues to furnish the indemnifying bonds or to pledge the securities required by the Authority. Any such trust agreement may set forth the rights of action by bondholders. In addition to the foregoing, any such trust agreement or resolution may contain such other provisions as the Authority deems reasonable and proper for the security of the bondholders. All expenses incurred in carrying out the provisions of a trust agreement or resolution may be treated as a part of the cost of the operation of the project.",-1),h=e("p",null,[t("1981, c. 569, § 2.1-234.15; 2001, c. "),e("a",{href:"http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844",target:"_blank",rel:"noreferrer"},"844"),t(".")],-1),c=[s,i,h];function d(u,l,p,f,m,g){return n(),r("div",null,c)}const v=o(a,[["render",d]]);export{y as __pageData,v as default};
