import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2284 Executive Director; appointment; duties.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2284.md","filePath":"vacode/2.2-2284.md"}'),o={name:"vacode/2.2-2284.md"},a=r('<h1 id="§-2-2-2284-executive-director-appointment-duties" tabindex="-1">§ 2.2-2284 Executive Director; appointment; duties. <a class="header-anchor" href="#§-2-2-2284-executive-director-appointment-duties" aria-label="Permalink to &quot;§ 2.2-2284 Executive Director; appointment; duties.&quot;">​</a></h1><p>The Director of the Department of Small Business and Supplier Diversity shall appoint the Executive Director of the Authority. The Executive Director shall administer, manage and direct the affairs and activities of the Authority in accordance with the policies and under the control and the direction of the Board and the Director of the Department of Small Business and Supplier Diversity. Except as otherwise stated in this article, the Executive Director shall approve all accounts for allowable expenses for the Authority or of any employee or consultant or other person providing services to the Board, and for expenses incidental to the operation of the Authority subject to approval of the Director of the Department of Small Business and Supplier Diversity. The Executive Director shall maintain and be custodian of all books, documents and papers of or filed with the Authority, including but not limited to the minute book or journal of the Authority, and of its official seal. The Executive Director may cause copies to be made of all minutes and other records and documents of the Authority and may in the place and stead of the Secretary of the Authority give certificates under seal of the Authority to the effect that such copies are true copies, and all persons dealing with the Authority may rely on such certificates. The Executive Director also shall perform such other duties as prescribed by the Board in carrying out the purposes of this article.</p><p>1984, c. 749, § 9-204; 1990, c. 387; 1996, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0589" target="_blank" rel="noreferrer">589</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?961+ful+CHAP0599" target="_blank" rel="noreferrer">599</a>; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2013, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?131+ful+CHAP0482" target="_blank" rel="noreferrer">482</a>.</p>',3),n=[a];function c(s,h,l,d,p,u){return t(),i("div",null,n)}const v=e(o,[["render",c]]);export{m as __pageData,v as default};
