import{_ as e,o as t,c as o,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2295 Regulations of the Authority.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2295.md","filePath":"vacode/2.2-2295.md"}'),i={name:"vacode/2.2-2295.md"},a=r('<h1 id="§-2-2-2295-regulations-of-the-authority" tabindex="-1">§ 2.2-2295 Regulations of the Authority. <a class="header-anchor" href="#§-2-2-2295-regulations-of-the-authority" aria-label="Permalink to &quot;§ 2.2-2295 Regulations of the Authority.&quot;">​</a></h1><p>Prior to carrying out the powers granted under §§ <a href="http://law.lis.virginia.gov/vacode/2.2-2293/" target="_blank" rel="noreferrer">2.2-2293</a> and <a href="http://law.lis.virginia.gov/vacode/2.2-2294/" target="_blank" rel="noreferrer">2.2-2294</a>, the Authority shall adopt rules and regulations governing its activities including but not limited to rules and regulations relating to the following:</p><ol><li><p>Procedures for the submission of requests or invitations and proposals for making loans to lenders and the investment in, purchase, assignment and sale of loans;</p></li><li><p>The reinvestment by a lender of the proceeds, or an equivalent amount, from any loan to a lender in loans to provide financing for eligible business in the Commonwealth;</p></li><li><p>Assurances that the eligible business to be financed will improve employment conditions or otherwise improve industrial development in the Commonwealth;</p></li><li><p>Rates, fees, charges, and other terms and conditions for originating or servicing loans in order to protect against realization of an excessive financial return or benefit by the originator or servicer;</p></li><li><p>The type and amount of collateral or security to be provided to assure repayment of loans to lenders made by the Authority;</p></li><li><p>The type of collateral, payment bonds, performance bonds or other security to be provided for any loans made by a lender for construction loans;</p></li><li><p>The nature and amount of fees to be charged by the Authority to provide for expenses and reserves of the Authority;</p></li><li><p>Standards and requirements for the allocation of available money among lenders and the determination of the maturities, terms, conditions and interest rates for loans made, purchased, sold, assigned or committed pursuant hereto;</p></li><li><p>Commitment requirements for financing by lenders involving money provided, directly or indirectly, by the Authority; or</p></li><li><p>Any other appropriate matters related to the duties or exercise of the Authority&#39;s powers.</p></li></ol><p>1984, c. 749, § 9-219; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2003, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?031+ful+CHAP0339" target="_blank" rel="noreferrer">339</a>.</p>',4),n=[a];function s(l,d,h,p,c,u){return t(),o("div",null,n)}const g=e(i,[["render",s]]);export{m as __pageData,g as default};
