import{_ as e,o as t,c as i,O as r}from"./chunks/framework.9482e208.js";const m=JSON.parse('{"title":"§ 2.2-2322 Deposit of money; expenditures; security for deposits.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2322.md","filePath":"vacode/2.2-2322.md"}'),o={name:"vacode/2.2-2322.md"},a=r('<h1 id="§-2-2-2322-deposit-of-money-expenditures-security-for-deposits" tabindex="-1">§ 2.2-2322 Deposit of money; expenditures; security for deposits. <a class="header-anchor" href="#§-2-2-2322-deposit-of-money-expenditures-security-for-deposits" aria-label="Permalink to &quot;§ 2.2-2322 Deposit of money; expenditures; security for deposits.&quot;">​</a></h1><p>A. All money of the Authority, except as otherwise authorized by law or this article, shall be deposited in accounts in banks or trust companies organized under the laws of the Commonwealth or in national banking associations located in Virginia or in savings institutions located in Virginia organized under the laws of the Commonwealth or the United States. The money in these accounts shall be paid by check signed by the Executive Director or any other officer or employee designated by the Authority. All deposits of money shall, if required by the Authority, be secured in a manner determined by the Authority to be prudent, and all banks, trust companies and savings institutions are authorized to give security for the deposits.</p><p>B. Funds of the Authority not needed for immediate use or disbursement, including any funds held in reserve, may be invested in (i) obligations or securities that are considered lawful investments for fiduciaries, both individual and corporate, as set forth in § <a href="http://law.lis.virginia.gov/vacode/2.2-4519/" target="_blank" rel="noreferrer">2.2-4519</a>, (ii) bankers&#39; acceptances, or (iii) repurchase agreements, reverse repurchase agreements, rate guarantee or investment agreements or other similar banking arrangements.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>, § 2.1-548.65; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>.</p>',4),n=[a];function s(d,c,l,h,p,u){return t(),i("div",null,n)}const g=e(o,[["render",s]]);export{m as __pageData,g as default};
