import{_ as e,o as r,c as a,O as t}from"./chunks/framework.9482e208.js";const g=JSON.parse('{"title":"§ 2.2-2323 Forms of accounts and records; annual reports; audit.","description":"","frontmatter":{},"headers":[],"relativePath":"vacode/2.2-2323.md","filePath":"vacode/2.2-2323.md"}'),o={name:"vacode/2.2-2323.md"},n=t('<h1 id="§-2-2-2323-forms-of-accounts-and-records-annual-reports-audit" tabindex="-1">§ 2.2-2323 Forms of accounts and records; annual reports; audit. <a class="header-anchor" href="#§-2-2-2323-forms-of-accounts-and-records-annual-reports-audit" aria-label="Permalink to &quot;§ 2.2-2323 Forms of accounts and records; annual reports; audit.&quot;">​</a></h1><p>The Authority shall maintain accounts and records showing the receipt and disbursement of funds from whatever source derived in a form as prescribed by the Auditor of Public Accounts. Such accounts and records shall correspond as nearly as possible to accounts and records maintained by corporate enterprises.</p><p>The accounts of the Authority shall be audited by the Auditor of Public Accounts, or his legally authorized representatives, as determined necessary by the Auditor of Public Accounts, and the costs of such audits shall be borne by the Authority. The Authority shall, following the close of each fiscal year, submit an annual report of its activities for the preceding year to the Governor. Each report shall set forth a complete operating and financial statement for the Authority during the fiscal year it covers.</p><p>1999, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0852" target="_blank" rel="noreferrer">852</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0881" target="_blank" rel="noreferrer">881</a>, § <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?991+ful+CHAP0002" target="_blank" rel="noreferrer">2</a>.1-548.66; 2001, c. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?011+ful+CHAP0844" target="_blank" rel="noreferrer">844</a>; 2018, cc. <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0057" target="_blank" rel="noreferrer">57</a>, <a href="http://lis.virginia.gov/cgi-bin/legp604.exe?181+ful+CHAP0307" target="_blank" rel="noreferrer">307</a>.</p>',4),i=[n];function s(c,l,d,h,u,f){return r(),a("div",null,i)}const _=e(o,[["render",s]]);export{g as __pageData,_ as default};
